const EMBEDDED_DATA = [{"id": 1, "question": "생명보험의 정신적 바탕은 무엇인가?", "choices": ["홍익인간", "상부상조", "박애주의"], "answer": 2, "explanation": "생명보험은 상부상조의 정신을 바탕으로 동질의 위험을 대비하는 것이다. 홍익인간은 빈출 오답이다.", "category": "기본이론"}, {"id": 2, "question": "우리나라에서 계(契)라는 제도가 시행된 시대는?", "choices": ["고려 시대", "삼한 시대", "조선 시대"], "answer": 2, "explanation": "삼한 시대에 계라는 제도가 시행되었다. 암기법으로 '삼계탕'으로 외운다.", "category": "기본이론"}, {"id": 3, "question": "신라 시대에 불교를 바탕으로 생겨난 제도는?", "choices": ["계", "보", "향약"], "answer": 2, "explanation": "신라 시대에는 불교라는 종교를 바탕으로 보(洑)가 생겨났다.", "category": "기본이론"}, {"id": 4, "question": "우리나라에 근대적 생명보험이 최초로 도입된 계기는?", "choices": ["조선생명보험 회사가 설립되면서", "강화도조약 때 일본이 대리점을 설치하면서", "방카슈랑스 제도가 시행되면서"], "answer": 2, "explanation": "강화도조약 때 일본이 우리나라에 대리점을 설치하면서 생명보험이 최초로 도입되었다. 조선생명보험 회사 설립은 빈출 오답이다.", "category": "기본이론"}, {"id": 5, "question": "1960년대 생명보험 시장의 특징으로 올바른 것은?", "choices": ["개인 보험이 성장하였다", "단체 보험이 성장하였다", "경쟁 체제에 돌입하였다"], "answer": 2, "explanation": "60년대는 개인이 보험을 가입하기 어려운 시절로 기업의 단체 보험이 성장했다. 개인 보험은 70년대에 성장했다.", "category": "기본이론"}, {"id": 6, "question": "1970년대 생명보험 시장의 특징은?", "choices": ["단체 보험이 성장하였다", "개인 보험이 성장하였다", "고도 성장기였다"], "answer": 2, "explanation": "70년대에는 개인 보험이 성장하였고 77년을 보험의 해로 지정하였다.", "category": "기본이론"}, {"id": 7, "question": "1980년대 생명보험 시장의 특징은?", "choices": ["경쟁 체제에 돌입하였다", "고도 성장기였다", "IMF 금융 위기를 겪었다"], "answer": 2, "explanation": "80년대는 가계 소득 증가에 따른 고도 성장기였다. 경쟁 체제 돌입은 90년대의 특징이다.", "category": "기본이론"}, {"id": 8, "question": "IMF 금융 위기로 허가가 취소된 보험 회사의 수는?", "choices": ["2개", "4개", "6개"], "answer": 2, "explanation": "90년대 IMF 금융 위기로 4개의 보험 회사가 허가 취소되었다.", "category": "기본이론"}, {"id": 9, "question": "방카슈랑스가 시행된 연도는?", "choices": ["2000년", "2003년", "2010년"], "answer": 2, "explanation": "방카슈랑스는 2003년부터 시행되었으며 은행에서 보험을 판매하는 금융기관 보험 대리점 역할을 한다.", "category": "기본이론"}, {"id": 10, "question": "사회보장 제도의 목적은?", "choices": ["만족할 만한 수준의 생활을 보장하기 위한 제도", "최저 생활을 보장하기 위한 제도", "고소득층의 자산을 보호하기 위한 제도"], "answer": 2, "explanation": "사회보장 제도는 국가가 국민의 최저 생활을 보장하기 위한 제도이다. 만족할 만한 수준은 오답이다.", "category": "기본이론"}, {"id": 11, "question": "다음 중 사회보험에 해당하지 않는 것은?", "choices": ["국민건강보험", "국민연금", "기초생활보장"], "answer": 3, "explanation": "사회보험은 국민건강보험, 국민연금, 산재보험, 고용보험이며 끝에 '보험'이 붙는다. 기초생활보장은 공공부조에 해당한다.", "category": "기본이론"}, {"id": 12, "question": "국민건강보험은 어떤 보험의 성격인가?", "choices": ["민영보험", "공영보험", "외국보험"], "answer": 2, "explanation": "국민건강보험 등 사회보험은 정부가 하는 공영보험의 성격이 있다. 민영보험은 보험 회사에서 판매하는 보험이다.", "category": "기본이론"}, {"id": 13, "question": "생명보험 자산운용 원칙에서 수익성과 관련하여 운영되어야 하는 기준은?", "choices": ["공시 이율 이상의 수익률", "적용 이율 이상의 수익률", "예정 이율 이상의 수익률"], "answer": 2, "explanation": "적용 이율 이상의 수익률로 운영할 수 있도록 운영되어야 한다.", "category": "기본이론"}, {"id": 14, "question": "생명보험 운용 자산에서 가장 많은 비중이 투자되는 곳은?", "choices": ["부동산", "유가 증권", "대출"], "answer": 2, "explanation": "유가 증권에 가장 많은 비중이 투자되고 있으며, 그중에서도 국공채에 가장 많이 투자된다.", "category": "기본이론"}, {"id": 15, "question": "합계 출산율이란?", "choices": ["저출산율", "가임기 여성이 평생 낳을 것으로 예상되는 평균 자녀의 수", "연간 출생아 수"], "answer": 2, "explanation": "합계 출산율이란 가임기 여성이 평생 낳을 것으로 예상되는 평균 자녀의 수이다.", "category": "기본이론"}, {"id": 16, "question": "고령 인구가 20%를 넘어가는 사회를 무엇이라 하는가?", "choices": ["고령화 사회", "고령 사회", "초고령 사회"], "answer": 3, "explanation": "7% 이상은 고령화 사회, 14% 이상은 고령 사회, 20% 이상은 초고령 사회이다.", "category": "기본이론"}, {"id": 17, "question": "우리나라의 주요 사망 원인 1위는?", "choices": ["심장질환", "암(악성 신생물)", "뇌혈관질환"], "answer": 2, "explanation": "사망 원인 1위는 암(악성 신생물)이다.", "category": "기본이론"}, {"id": 18, "question": "잉여 소득이 발생하는 시기는?", "choices": ["사회 초년기", "중년기", "노년기"], "answer": 2, "explanation": "소득이 지출을 넘어서는 잉여 소득이 발생하는 시기는 중년기이다.", "category": "기본이론"}, {"id": 19, "question": "대한민국의 자산 비중 특징으로 올바른 것은?", "choices": ["선진국 대비 금융자산 비중이 높다", "선진국 대비 부동산 자산 비중이 높다", "선진국과 자산 비중이 유사하다"], "answer": 2, "explanation": "대한민국은 선진국 대비 부동산 자산의 비중이 높고 금융자산 비중은 낮다.", "category": "기본이론"}, {"id": 20, "question": "자본시장과 금융투자에 관한 법률 통과 이후 업종간 구분은 어떻게 되었는가?", "choices": ["업종 구분이 강해지고 상품이 단순화되었다", "업종 구분이 약해지고 경계가 허물어졌다", "업종 구분에 변화가 없다"], "answer": 2, "explanation": "자본시장법 통과로 업종간 구분이 약해지며 서로의 경계가 허물어졌다.", "category": "기본이론"}, {"id": 21, "question": "대수의 법칙에서 관찰의 횟수가 늘어날수록 어떻게 되는가?", "choices": ["편차가 커진다", "확률의 정확성이 높아진다", "위험이 증가한다"], "answer": 2, "explanation": "관찰의 횟수가 늘어날수록 확률의 정확성이 높아진다. 편차가 커진다는 빈출 오답이다.", "category": "기본이론"}, {"id": 22, "question": "우리나라에서 현재 사용하고 있는 생명표는?", "choices": ["제11회 국민 생명표", "제11회 경험 생명표", "제10회 경험 생명표"], "answer": 2, "explanation": "2024년부터 제11회 경험 생명표를 사용하고 있다. 국민 생명표는 오답이다.", "category": "기본이론"}, {"id": 23, "question": "수지상등의 원칙에서 가정하는 기본 조건은?", "choices": ["모든 연령의 피보험자가 모든 보험 종류를 동시에 계약", "동일 연령의 피보험자가 같은 보험 종류를 동시에 계약", "다양한 연령의 피보험자가 다른 보험 종류를 계약"], "answer": 2, "explanation": "동일 연령의 피보험자가 같은 보험 종류를 동시에 계약했을 경우를 가정한다.", "category": "기본이론"}, {"id": 24, "question": "보험 계약자의 자격 요건은?", "choices": ["건강 상태", "소득", "나이"], "answer": 2, "explanation": "계약자는 보험료를 내는 사람이므로 자격 요건은 소득이다.", "category": "기본이론"}, {"id": 25, "question": "피보험자의 자격 요건은?", "choices": ["소득", "건강", "재산"], "answer": 2, "explanation": "피보험자는 보험 사고의 대상이므로 자격 요건은 건강이다.", "category": "기본이론"}, {"id": 26, "question": "피보험자를 다른 사람으로 변경할 수 있는가?", "choices": ["회사의 승낙이 있으면 가능하다", "절대 변경이 불가능하다", "피보험자의 동의가 있으면 가능하다"], "answer": 2, "explanation": "최초에 가입한 피보험자가 아닌 다른 피보험자로는 절대 변경이 불가능하다.", "category": "기본이론"}, {"id": 27, "question": "수익자의 자격 요건은?", "choices": ["건강한 사람", "소득이 있는 사람", "사람이기만 하면 된다(자연인, 법인 모두 가능)"], "answer": 3, "explanation": "수익자는 보험금을 받기만 하면 되므로 자연인이든 법인이든 사람이기만 하면 된다.", "category": "기본이론"}, {"id": 28, "question": "수익자 변경 시 필요한 것은?", "choices": ["회사의 승낙", "피보험자의 동의", "금융위원회의 인가"], "answer": 2, "explanation": "수익자 변경 시 회사의 승낙은 불필요하지만 피보험자의 동의는 필요하다.", "category": "기본이론"}, {"id": 29, "question": "피보험자의 계약 전 알릴 의무 대상은?", "choices": ["소득", "건강", "주소"], "answer": 2, "explanation": "피보험자는 건강에 대해서 계약 전 알릴 의무가 있다.", "category": "기본이론"}, {"id": 30, "question": "현재 보험료 산출에 사용하고 있는 방식은?", "choices": ["3이원 방식", "현금흐름 방식", "표준 방식"], "answer": 2, "explanation": "3이원 방식은 예전 방식이고 현재는 현금흐름 방식을 사용하고 있다.", "category": "기본이론"}, {"id": 31, "question": "3이원 방식의 세 가지 이익의 원천은?", "choices": ["예정 위험률, 예정 이율, 예정 사업비율", "공시 이율, 표준 이율, 적용 이율", "수익률, 유지율, 해지율"], "answer": 1, "explanation": "3이원 방식은 예정 위험률, 예정 이율, 예정 사업비율의 세 가지 이익의 원천에 따라 보험료를 계산한다.", "category": "기본이론"}, {"id": 32, "question": "청약 철회가 가능한 기간은?", "choices": ["증권 수령일로부터 30일 이내", "증권 수령일로부터 15일 이내, 최대 청약일로부터 30일", "청약일로부터 15일 이내"], "answer": 2, "explanation": "증권을 받은 날로부터 15일 이내에 청약 철회가 가능하고 최대 청약일로부터 30일까지 가능하다.", "category": "기본이론"}, {"id": 33, "question": "청약 철회가 불가능한 경우가 아닌 것은?", "choices": ["전문 계약자", "진단 계약", "무진단 계약"], "answer": 3, "explanation": "전문 계약자, 진단 계약, 보험기간 91일 이내 계약은 청약 철회가 불가능하다. 무진단 계약은 철회 가능하다.", "category": "기본이론"}, {"id": 34, "question": "청약 철회 시 보험료 반환 기한은?", "choices": ["3영업일 이내", "5영업일 이내", "10영업일 이내"], "answer": 1, "explanation": "보험료를 납입한 경우 3영업일 이내에 돌려주어야 한다.", "category": "기본이론"}, {"id": 35, "question": "보험 계약의 성립에 관한 설명으로 올바른 것은?", "choices": ["보험료를 납입해야 계약이 성립한다", "계약자의 청약과 회사의 승낙이 있어야 계약이 성립한다", "피보험자의 동의가 있어야 계약이 성립한다"], "answer": 2, "explanation": "보험 계약은 낙성 계약으로 계약자의 청약과 회사의 승낙이 있어야 성립한다.", "category": "기본이론"}, {"id": 36, "question": "무진단 계약의 거절 가능 기간은?", "choices": ["청약일로부터 15일 이내", "청약일로부터 30일 이내", "청약일로부터 60일 이내"], "answer": 2, "explanation": "무진단 계약은 청약일로부터 30일 이내에 거절이 가능하다.", "category": "기본이론"}, {"id": 37, "question": "회사가 30일 이내에 거절 의사를 표현하지 않은 경우 어떻게 되는가?", "choices": ["자동으로 청약이 취소된다", "자동으로 승낙한 것으로 본다", "재심사를 진행한다"], "answer": 2, "explanation": "30일 이내에 미거절한 경우에는 자동으로 승낙한 것으로 본다.", "category": "기본이론"}, {"id": 38, "question": "보험 증권에 표기되지 않는 내용은?", "choices": ["보험의 목적", "피보험자 및 수익자 정보", "설계사 수수료"], "answer": 3, "explanation": "설계사 수수료 또는 모집 수당은 보험 증권에 표시되지 않는다.", "category": "기본이론"}, {"id": 39, "question": "보장 개시일(효력 발생일)은 언제인가?", "choices": ["회사가 승낙한 날", "1회 보험료를 납입한 날", "청약서를 작성한 날"], "answer": 2, "explanation": "보험 계약의 효력은 1회 보험료를 납입한 날부터 발생한다.", "category": "기본이론"}, {"id": 40, "question": "사망보험에서 피보험자의 서면 동의를 받지 않은 경우 계약은?", "choices": ["취소 가능", "무효", "해지 가능"], "answer": 2, "explanation": "사망보험에서 피보험자와 계약자가 다른 경우 피보험자의 서면 동의를 받지 않으면 무효이다.", "category": "기본이론"}, {"id": 41, "question": "15세 미만자의 사망보험 계약은?", "choices": ["동의가 있으면 유효", "무효", "취소 가능"], "answer": 2, "explanation": "15세 미만자, 심신 상실자, 심신 박약자의 사망보험은 동의 유무와 상관없이 무효이다.", "category": "기본이론"}, {"id": 42, "question": "심신 박약자가 단체보험의 피보험자가 될 경우 계약은?", "choices": ["무효", "의사 능력이 있는 경우 유효", "조건부 취소 가능"], "answer": 2, "explanation": "심신 박약자가 단체보험의 피보험자가 될 경우 의사 능력이 있으면 유효한 계약으로 본다.", "category": "기본이론"}, {"id": 43, "question": "3대 기본 지키기에 해당하지 않는 것은?", "choices": ["청약서 사본 전달", "약관 전달 및 설명", "보험료 할인 안내"], "answer": 3, "explanation": "3대 기본 지키기는 청약서 사본 전달, 약관 전달 및 설명, 계약자 자필 서명이다. (청약자)", "category": "기본이론"}, {"id": 44, "question": "3대 기본 지키기를 위반한 경우 계약 취소 가능 기간은?", "choices": ["1개월 이내", "3개월 이내", "6개월 이내"], "answer": 2, "explanation": "3대 기본 지키기 위반 시 3개월 이내에 계약을 취소할 수 있다.", "category": "기본이론"}, {"id": 45, "question": "계약자가 계약전 알릴 의무를 위반해도 회사가 해지할 수 없는 경우가 아닌 것은?", "choices": ["회사가 가입 당시 거짓말한 것을 알고 있었던 경우", "보장개시일로부터 보험금 지급 사유 없이 2년 경과한 경우", "계약 체결일로부터 1년이 지난 경우"], "answer": 3, "explanation": "계약 체결일로부터 3년이 지난 경우 해지할 수 없다. 1년은 오답이다.", "category": "기본이론"}, {"id": 46, "question": "일반 실종의 경우 법원에서 사망으로 보는 기간은?", "choices": ["1년간 생사 불분명", "3년간 생사 불분명", "5년간 생사 불분명"], "answer": 3, "explanation": "일반 실종은 5년간 생사가 불분명할 경우 법원에서 사망으로 본다.", "category": "기본이론"}, {"id": 47, "question": "특별 실종(선박 침몰, 항공기 추락 등)의 경우 사망으로 보는 기간은?", "choices": ["1년간 생사 불분명 시 법원에서 사망으로 봄", "3년간 생사 불분명 시 법원에서 사망으로 봄", "지체없이 관공서에서 인정"], "answer": 1, "explanation": "특별 실종은 1년간 생사가 불분명할 경우 법원에서 사망으로 본다.", "category": "기본이론"}, {"id": 48, "question": "인정 사망의 경우 사망 인정은 어떻게 이루어지는가?", "choices": ["5년간 생사 불분명 시 법원에서 인정", "1년간 생사 불분명 시 법원에서 인정", "지체없이 관공서에서 인정"], "answer": 3, "explanation": "수해나 산불 등의 경우 지체없이 관공서에서 사망을 인정한다.", "category": "기본이론"}, {"id": 49, "question": "한시적 장애에서 5년 이상인 경우 지급하는 비율은?", "choices": ["10%", "20%", "50%"], "answer": 2, "explanation": "한시적 장애가 5년 이상인 경우 20%를 지급한다.", "category": "기본이론"}, {"id": 50, "question": "보험금 청구 시 일반적인 지급 기한은?", "choices": ["3영업일 이내", "5영업일 이내", "10영업일 이내"], "answer": 1, "explanation": "보험금 청구를 하면 3영업일 이내에 지급한다.", "category": "기본이론"}, {"id": 51, "question": "가지급 제도에서 추정 보험금의 몇 % 이내를 미리 지급하는가?", "choices": ["30% 이내", "50% 이내", "70% 이내"], "answer": 2, "explanation": "가지급 제도는 추정되는 보험금의 50% 이내의 금액을 미리 지급하는 제도이다.", "category": "기본이론"}, {"id": 52, "question": "보험료 미납 시 납입 최고(독촉) 기간은?", "choices": ["7일 이상", "14일 이상", "30일 이상"], "answer": 2, "explanation": "보험료 미납 시 14일 이상의 납입 최고 기간을 준다. 보험기간 1년 미만은 7일 이상이다.", "category": "기본이론"}, {"id": 53, "question": "미납으로 인한 해지 후 부활 가능 기간은?", "choices": ["해지된 날로부터 1년 이내", "해지된 날로부터 3년 이내", "해지된 날로부터 5년 이내"], "answer": 2, "explanation": "해지된 날로부터 3년 이내에 부활이 가능하며, 해약 환급금을 받지 않은 경우에만 가능하다.", "category": "기본이론"}, {"id": 54, "question": "부활 시 신계약과 동일한 절차를 거치는 이유는?", "choices": ["비용 절감을 위해서", "역선택을 방지하기 위해서", "법률적 요건을 충족하기 위해서"], "answer": 2, "explanation": "부활 시 역선택을 방지하기 위해 신계약과 동일한 절차를 거친다.", "category": "기본이론"}, {"id": 55, "question": "특별 부활에서 수익자에게 제도를 알려야 하는 기간은?", "choices": ["3일 이내", "7일 이내", "15일 이내"], "answer": 2, "explanation": "강제 집행 등으로 계약이 해지된 경우 회사는 7일 이내에 수익자에게 부활 제도를 알려야 한다.", "category": "기본이론"}, {"id": 56, "question": "부당한 계약 전환으로 인정되는 기존 계약과 신계약 간의 간격은?", "choices": ["15일 이내", "1개월 이내", "3개월 이내"], "answer": 2, "explanation": "기존 계약과 신계약 간의 간격이 1개월 이내인 경우 부당한 계약 전환으로 인정된다.", "category": "기본이론"}, {"id": 57, "question": "부당한 계약 전환 시 기존 계약의 부활 청구 가능 기간은?", "choices": ["3개월 이내", "6개월 이내", "1년 이내"], "answer": 2, "explanation": "기존 계약이 소멸한 날로부터 6개월 이내에 기존 계약의 부활 청구가 가능하다.", "category": "기본이론"}, {"id": 58, "question": "보험 계약에서 감액이란?", "choices": ["보험료를 줄이는 행위", "보험금(보장)을 줄이는 행위", "보험 기간을 줄이는 행위"], "answer": 2, "explanation": "감액은 보장 즉 보험금을 줄이는 행위이며, 감액된 부분에 대해서는 해약 환급금을 지급한다.", "category": "기본이론"}, {"id": 59, "question": "사망보험에서 피보험자의 서면동의 철회는 어떻게 이루어지는가?", "choices": ["소급하여 철회한다", "장래를 향하여 철회한다", "과거와 장래 모두에 대해 철회한다"], "answer": 2, "explanation": "피보험자는 장래를 향하여 서면동의 철회가 가능하다. 소급하여 철회한다는 오답이다.", "category": "기본이론"}, {"id": 60, "question": "보험금 청구권의 소멸시효는?", "choices": ["2년", "3년", "5년"], "answer": 2, "explanation": "보험금(금, 반환)은 3년 이내에 권리 행사가 가능하고, 보험료(료)는 2년이다.", "category": "기본이론"}, {"id": 61, "question": "보험회사가 손해배상 책임을 지지 않는 경우는?", "choices": ["임직원의 잘못으로 인한 손해", "설계사의 잘못으로 인한 손해", "중개사로 인한 손해"], "answer": 3, "explanation": "중개사로 인한 손해 및 해당 계약과 무관한 사고에 대해서는 보험회사가 책임을 지지 않는다.", "category": "기본이론"}, {"id": 62, "question": "보험 계약 대출의 담보는?", "choices": ["납입 보험료", "해약 환급금", "사망 보험금"], "answer": 2, "explanation": "보험 계약 대출은 해약 환급금의 범위 내에서 가능하다.", "category": "기본이론"}, {"id": 63, "question": "약관의 내용이 명확하지 않은 경우 해석 기준은?", "choices": ["보험회사에 유리하게 해석", "계약자에게 유리하게 해석", "법원에 판단을 의뢰"], "answer": 2, "explanation": "약관의 내용이 애매모호한 경우 계약자에게 유리하게 해석한다.", "category": "기본이론"}, {"id": 64, "question": "언더라이팅의 목적은?", "choices": ["보험금 지급 사고 조사", "피보험자의 위험을 선택하여 역선택 방지", "보험 상품 개발"], "answer": 2, "explanation": "언더라이팅은 피보험자의 위험을 선택하여 보험료 및 보험금 한도를 결정하고 역선택을 방지하는 것이 목적이다.", "category": "기본이론"}, {"id": 65, "question": "소득 수준에 대해 물어보는 언더라이팅의 종류는?", "choices": ["환경적 언더라이팅", "신체적 언더라이팅", "재정적 언더라이팅"], "answer": 3, "explanation": "소득 수준에 대해 물어보는 것은 재정적 언더라이팅이다.", "category": "기본이론"}, {"id": 66, "question": "체증 위험(위험이 기간 경과에 따라 증가하는 위험)에 적용하는 방법은?", "choices": ["보험금 삭감", "보험료 할증", "보험 거절"], "answer": 2, "explanation": "체증 위험(항상성 위험 포함)의 경우 보험료 할증 방법을 선택한다.", "category": "기본이론"}, {"id": 67, "question": "체감성 위험(위험이 기간 경과에 따라 감소하는 위험)에 적용하는 방법은?", "choices": ["보험료 할증", "보험금 삭감", "가입 거절"], "answer": 2, "explanation": "체감성 위험의 경우 일정 기간 보험금을 적게 주는 보험금 삭감 방식을 사용한다.", "category": "기본이론"}, {"id": 68, "question": "클레임 절차에서 '확인'이라는 글자가 나오면 어떤 단계인가?", "choices": ["준비 단계", "진행 단계", "종결 단계"], "answer": 2, "explanation": "확인이라는 글자가 나오면 진행 단계이다. 다만 '계약 정보 확인'만 준비 단계이다.", "category": "기본이론"}, {"id": 69, "question": "역선택과 도덕적 해이의 공통점은?", "choices": ["보험금 사기", "정보의 불균형", "보험료 미납"], "answer": 2, "explanation": "역선택과 도덕적 해이 모두 가입자와 보험회사 간의 정보의 불균형에서 발생한다.", "category": "기본이론"}, {"id": 70, "question": "역선택과 도덕적 해이의 차이점으로 올바른 것은?", "choices": ["역선택은 계약 이후, 도덕적 해이는 계약 이전의 정보 불균형", "역선택은 계약 이전, 도덕적 해이는 계약 이후의 정보 불균형", "둘 다 계약 이후에 발생"], "answer": 2, "explanation": "역선택은 계약 체결 이전의 정보 불균형이고, 도덕적 해이는 체결 이후의 정보 불균형이다.", "category": "기본이론"}, {"id": 71, "question": "보험 사기 방지 특별법에 따른 처벌은?", "choices": ["5년 이하 징역 또는 2천만 원 이하 벌금", "10년 이하 징역 또는 5천만 원 이하 벌금", "3년 이하 징역 또는 1천만 원 이하 벌금"], "answer": 2, "explanation": "보험 사기 방지 특별법에 따라 10년 이하 징역 또는 5천만 원 이하 벌금에 처한다. 과태료나 과징금은 정답이 될 수 없다.", "category": "기본이론"}, {"id": 72, "question": "보험 사기 방지 활동에서 수사를 담당하는 기관은?", "choices": ["금융감독원", "경찰청", "생명보험 협회"], "answer": 2, "explanation": "수사는 경찰청, 신고는 금감원, 홍보는 협회가 담당한다.", "category": "기본이론"}, {"id": 73, "question": "유명인의 자살에 심리적 동조를 일으켜 따라하는 현상을 무엇이라 하는가?", "choices": ["피그말리온 효과", "베르테르 효과", "나비 효과"], "answer": 2, "explanation": "유명인의 자살에 심리적 동조를 일으켜 따라하는 것을 베르테르 효과라고 한다.", "category": "기본이론"}, {"id": 74, "question": "설계사의 업무는 대리 업무인가, 중개 업무인가?", "choices": ["대리 업무", "중개 업무", "위탁 업무"], "answer": 2, "explanation": "설계사는 중개 업무를 하며 소비자(계약자)의 입장을 대변하는 성격이 짙다.", "category": "기본이론"}, {"id": 75, "question": "설계사와 대리점의 등록 업무를 위탁받아 수행하는 기관은?", "choices": ["금융감독원", "금융위원회", "보험 협회"], "answer": 3, "explanation": "설계사와 대리점은 보험 협회에, 중개사는 금융감독원에 등록 업무가 위탁되어 있다.", "category": "기본이론"}, {"id": 76, "question": "설계사의 소속 형태는?", "choices": ["여러 회사에 다중 소속(일사 전속이 아님)", "한 회사에 전속(일사 전속)", "독립적으로 계약 체결"], "answer": 2, "explanation": "설계사는 한 회사에 전속되어 일하는 일사 전속이다. 대리점은 다중 소속, 중개사는 독립적이다.", "category": "기본이론"}, {"id": 77, "question": "설계사 등록은 연수 과정 이수 후 몇 년 이내에 가능한가?", "choices": ["6개월 이내", "1년 이내", "2년 이내"], "answer": 2, "explanation": "설계사는 연수 과정 이수 후 1년 이내에 등록해야 한다. 대리점은 2년 이내이다.", "category": "기본이론"}, {"id": 78, "question": "부정한 등록을 한 설계사에 대한 처벌은?", "choices": ["6개월 이하 징역 또는 500만 원 이하 벌금", "1년 이하 징역 또는 1천만 원 이하 벌금", "3년 이하 징역 또는 3천만 원 이하 벌금"], "answer": 2, "explanation": "부정한 등록을 한 경우 1년 이하 징역 또는 1천만 원 이하 벌금에 처한다.", "category": "기본이론"}, {"id": 79, "question": "교차 모집에 대한 설명으로 올바른 것은?", "choices": ["소속 회사 외 같은 종류의 보험 회사 선택 가능", "소속 회사 외 다른 종류의 보험 회사 선택 가능", "대리점과 중개사도 교차 모집 대상이다"], "answer": 2, "explanation": "교차 모집은 소속 회사 외에 다른 종류의 보험 회사를 선택하여 가능하다. 같은 종류는 불가능하다.", "category": "기본이론"}, {"id": 80, "question": "설계사가 직접 협회에 신청할 수 있는 것은?", "choices": ["등록", "말소", "등록과 말소 모두"], "answer": 2, "explanation": "등록은 회사를 통해서만 가능하지만 말소는 설계사가 직접 협회에 신청 가능하다.", "category": "기본이론"}, {"id": 81, "question": "설계사의 등록 취소 및 영업 정지를 위해 금융위원회가 실시해야 하는 것은?", "choices": ["감사", "청문", "조사"], "answer": 2, "explanation": "설계사의 등록을 취소하거나 영업 정지를 명하기 위해서는 금융위원회가 청문을 실시해야 한다.", "category": "기본이론"}, {"id": 82, "question": "금융기관 보험 대리점이 될 수 없는 기관은?", "choices": ["저축은행", "우체국", "산업은행"], "answer": 2, "explanation": "겸영 여신업자, 우체국, 새마을 금고는 금융기관 보험 대리점이 될 수 없다.", "category": "기본이론"}, {"id": 83, "question": "금융기관 보험 대리점에서 판매 불가능한 상품은?", "choices": ["생사 혼합 보험", "일반 개인 보장성 보험", "신용생명 보험"], "answer": 2, "explanation": "일반 개인 보장성, 일반 장기 보장성, 개인용 자동차 보험, 사망 특약이 포함된 제3보험은 판매 불가능하다.", "category": "기본이론"}, {"id": 84, "question": "협정 위반 시 신고하는 곳과 기한은?", "choices": ["금융감독원에 1년 내에 신고", "생명보험 협회에 6개월 내에 신고", "금융위원회에 3개월 내에 신고"], "answer": 2, "explanation": "위반 행위 발생 시 6개월 내에 생명보험 협회에 신고한다. 인가는 금융위원회이다.", "category": "기본이론"}, {"id": 85, "question": "우수 인증 설계사의 인증 기관은?", "choices": ["금융감독원", "생명보험 협회", "금융위원회"], "answer": 2, "explanation": "우수 인증 설계사는 2008년부터 생명보험 협회의 엄격한 인증 기준을 충족하는 경우에 부여한다.", "category": "기본이론"}, {"id": 86, "question": "우수 인증 설계사의 동일 회사 근무 기간 요건은?", "choices": ["1년 이상", "3년 이상", "5년 이상"], "answer": 2, "explanation": "동일 회사에서 3년 이상 근무한 설계사 또는 개인 대리점이 대상이다. 법인 대리점은 제외된다.", "category": "기본이론"}, {"id": 87, "question": "우수 인증 설계사의 13회차 유지율 기준은?", "choices": ["80% 이상", "85% 이상", "90% 이상"], "answer": 3, "explanation": "13회차 유지율 90% 이상, 25회차 유지율 80% 이상이어야 한다.", "category": "기본이론"}, {"id": 88, "question": "예금자 보호의 보장 한도는?", "choices": ["5천만 원", "1억 원", "2억 원"], "answer": 2, "explanation": "금융 기관 파산시 1억 원 한도 내에서 예금보험공사가 지급한다.", "category": "기본이론"}, {"id": 89, "question": "보험에서 예금자 보호 기준이 되는 금액은?", "choices": ["납입 보험료", "해약 환급금", "사망 보험금"], "answer": 2, "explanation": "보험은 해약 환급금을 기준으로 예금자 보호 금액이 책정된다.", "category": "기본이론"}, {"id": 90, "question": "퇴직 연금 중 예금자 보호가 되지 않는 것은?", "choices": ["확정 기여형(DC)", "확정 급여형(DB)", "개인형 퇴직연금(IRP)"], "answer": 2, "explanation": "확정 급여형(DB)은 사장님이 책임지는 법인의 돈이라 예금자 보호가 되지 않는다.", "category": "기본이론"}, {"id": 91, "question": "변액 보험에서 예금자 보호가 되는 부분은?", "choices": ["주계약", "특약 및 최저 보증 금액", "전체 계약"], "answer": 2, "explanation": "변액 보험은 주계약은 보호되지 않고 특약 및 최저 보증하는 금액에 대해서만 예금자 보호가 가능하다.", "category": "기본이론"}, {"id": 92, "question": "금융분쟁 조정 위원회가 설치되어 있는 기관은?", "choices": ["금융위원회", "금융감독원", "생명보험 협회"], "answer": 2, "explanation": "금융분쟁 조정 위원회는 금융감독원 내에 설치되어 있다.", "category": "기본이론"}, {"id": 93, "question": "금융분쟁 조정 위원회의 합의 권고 기간과 조정 기간은?", "choices": ["합의 15일, 조정 30일", "합의 30일, 조정 60일", "합의 60일, 조정 90일"], "answer": 2, "explanation": "합의는 30일, 조정은 60일이다. 수락 시 재판상 화해와 동일한 효력을 가진다.", "category": "기본이론"}, {"id": 94, "question": "보험 가입 조회 서비스 '내보험 찾아줌'을 운영하는 기관은?", "choices": ["금융감독원", "생명보험 협회", "금융위원회"], "answer": 2, "explanation": "생명보험 협회에서 '내보험 찾아줌' 서비스를 운영하고 있다.", "category": "기본이론"}, {"id": 95, "question": "대리청구인 지정 제도의 기본 조건은?", "choices": ["계약자와 피보험자가 동일한 계약", "계약자, 피보험자, 수익자가 모두 동일한 계약", "계약자와 수익자가 동일한 계약"], "answer": 2, "explanation": "대리청구인 지정 제도는 계약자, 피보험자, 수익자가 모두 동일인인 계약에서 가능하다.", "category": "기본이론"}, {"id": 96, "question": "보험 민원을 접수할 수 있는 곳이 아닌 것은?", "choices": ["보험회사", "생명보험 협회", "금융감독원"], "answer": 2, "explanation": "민원은 보험회사, 금융감독원, 한국 소비자 보호원에 접수한다. 협회는 민원 접수 기관이 아니다.", "category": "기본이론"}, {"id": 97, "question": "보험 광고의 유효 기간은?", "choices": ["6개월", "1년", "2년"], "answer": 2, "explanation": "보험 광고의 유효 기간은 1년으로 통일되어 있다.", "category": "기본이론"}, {"id": 98, "question": "보험회사의 결산 공시 기한과 공시 기간은?", "choices": ["결산일로부터 2개월 이내, 2년간 공시", "결산일로부터 3개월 이내, 3년간 공시", "결산일로부터 6개월 이내, 5년간 공시"], "answer": 2, "explanation": "결산 공시는 결산일로부터 3개월 이내에 하여 3년간 공시한다.", "category": "기본이론"}, {"id": 99, "question": "비교 공시를 할 수 있는 기관은?", "choices": ["보험회사", "생명보험 협회", "금융감독원"], "answer": 2, "explanation": "비교 공시는 협회만 할 수 있다. 보험회사와 금융감독원은 할 수 없다.", "category": "기본이론"}, {"id": 100, "question": "보험 산업이 성숙 산업일수록 중요한 것은?", "choices": ["신규 고객 확보", "기존 고객의 만족도 제고", "상품 다양화"], "answer": 2, "explanation": "성숙 산업일수록 기존 고객의 만족도를 높이는 것(만족도 제고)이 중요하다.", "category": "기본이론"}, {"id": 101, "question": "보험 민원의 발생 형태는?", "choices": ["본사와 소비자의 직접 계약 형태", "설계사나 대리점이 끼는 간접 계약 형태", "온라인 계약 형태"], "answer": 2, "explanation": "보험회사 본사와 소비자가 직접 계약하는 형태가 아닌 중간에 설계사나 대리점이 끼는 간접 계약 형태이기 때문에 민원이 발생한다.", "category": "기본이론"}, {"id": 102, "question": "우리나라 개인 소득세의 과세 방식은?", "choices": ["포괄주의", "열거주의", "신고주의"], "answer": 2, "explanation": "우리나라는 개인 소득세에 대해 열거주의를 채택하고 있다. 나열한 것만 세금을 내면 된다.", "category": "기본이론"}, {"id": 103, "question": "퇴직 소득과 양도 소득에 적용되는 과세 방식은?", "choices": ["종합 과세", "분류 과세", "분리 과세"], "answer": 2, "explanation": "퇴직 소득과 양도 소득은 장기간에 걸쳐 발생한 소득이므로 분류 과세를 적용한다.", "category": "기본이론"}, {"id": 104, "question": "금융 소득(이자+배당)이 연간 얼마 이하일 때 분리 과세가 적용되는가?", "choices": ["1천만 원", "2천만 원", "3천만 원"], "answer": 2, "explanation": "금융 소득이 연간 2천만 원 이하인 경우 분리 과세 14%를 적용한다.", "category": "기본이론"}, {"id": 105, "question": "저축성 보험의 보험 차익은 어떤 소득으로 분류되는가?", "choices": ["사업 소득", "이자 소득", "기타 소득"], "answer": 2, "explanation": "저축성 보험의 보험 차익은 이자 소득으로 분류된다(유형별 포괄주의).", "category": "기본이론"}, {"id": 106, "question": "보험 설계사의 소득은 어떤 소득으로 분류되는가?", "choices": ["근로 소득", "사업 소득", "기타 소득"], "answer": 2, "explanation": "보험 설계사의 소득은 근로 소득이 아닌 사업 소득으로 분류된다.", "category": "기본이론"}, {"id": 107, "question": "연금 소득이 1,500만 원 이하인 경우 저율 분리 과세율은?", "choices": ["1%~3%", "3%~5%", "5%~10%"], "answer": 2, "explanation": "1,500만 원 이하 연금 소득은 수령 나이에 따라 3%~5%의 저율 분리 과세를 선택할 수 있다.", "category": "기본이론"}, {"id": 108, "question": "연금 수령 한도 공식에서 수령 연차가 몇 년 이상이면 한도를 적용하지 않는가?", "choices": ["5년 이상", "10년 이상", "11년 이상"], "answer": 3, "explanation": "수령 연차가 11년 이상인 경우 한도를 적용하지 않는다.", "category": "기본이론"}, {"id": 109, "question": "보장성 보험의 세액 공제 대상은?", "choices": ["종합소득자 모두", "근로소득자", "사업소득자"], "answer": 2, "explanation": "보장성 보험의 세액 공제는 계약자가 근로소득자인 경우에만 대상이 된다.", "category": "기본이론"}, {"id": 110, "question": "장애인 전용 보장성 보험의 세액 공제율은?", "choices": ["12%", "15%", "20%"], "answer": 2, "explanation": "장애인 전용 보장성 보험은 15%의 세액 공제를 받을 수 있다. 일반 보장성 보험은 12%이다.", "category": "기본이론"}, {"id": 111, "question": "연금 계좌(연금저축+퇴직연금)의 세액 공제 합산 한도는?", "choices": ["600만 원", "900만 원", "1,200만 원"], "answer": 2, "explanation": "연금 계좌의 세액 공제 합산 한도는 900만 원이며, 연금 저축은 600만 원으로 제한한다.", "category": "기본이론"}, {"id": 112, "question": "연금보험(저축성 보험)의 비과세 기본 조건에서 유지 기간은?", "choices": ["5년 이상", "10년 이상", "15년 이상"], "answer": 2, "explanation": "저축성 보험은 10년 이상 유지하면 비과세가 적용된다.", "category": "기본이론"}, {"id": 113, "question": "저축성 보험 비과세 요건에서 월납 시 월 보험료 한도는?", "choices": ["100만 원 이하", "150만 원 이하", "200만 원 이하"], "answer": 2, "explanation": "월납인 경우 월 보험료 150만 원 이하까지 비과세가 가능하다.", "category": "기본이론"}, {"id": 114, "question": "법정 상속 순위 1순위는?", "choices": ["배우자", "직계 비속(자녀)", "직계 존속(부모)"], "answer": 2, "explanation": "상속 순위는 1순위 직계 비속, 2순위 직계 존속, 3순위 형제자매, 4순위 사촌이다. 배우자는 순위에 포함되지 않고 1순위 또는 2순위와 공동으로 받는다.", "category": "기본이론"}, {"id": 115, "question": "배우자가 공동 상속 시 받는 비율은?", "choices": ["동일한 비율", "1.5배", "2배"], "answer": 2, "explanation": "배우자는 1순위, 2순위와 공동으로 받을 때 1.5배를 받는다.", "category": "기본이론"}, {"id": 116, "question": "금융재산 상속 공제에서 1억 초과 시 공제율과 최대 공제 한도는?", "choices": ["10%, 최대 1억", "20%, 최대 2억", "30%, 최대 3억"], "answer": 2, "explanation": "1억 초과 시 20%를 공제해 주며 최대 공제 한도는 2억이다.", "category": "기본이론"}, {"id": 117, "question": "장애인을 수익자로 하는 보험의 증여세 비과세 한도는?", "choices": ["연간 2천만 원", "연간 4천만 원", "연간 5천만 원"], "answer": 2, "explanation": "장애인을 수익자로 하는 보험의 보험금에 대해 연간 4천만 원까지 증여세를 비과세한다.", "category": "기본이론"}, {"id": 118, "question": "보험회사가 설계사 수당에서 원천 징수하는 세율은?", "choices": ["2%", "3%", "5%"], "answer": 2, "explanation": "보험회사는 매월 설계사 모집 수당에 대해 3%를 원천 징수한다.", "category": "기본이론"}, {"id": 119, "question": "연금 저축의 세액 공제 한도는?", "choices": ["400만 원", "600만 원", "900만 원"], "answer": 2, "explanation": "연금 저축의 세액 공제 한도는 600만 원으로 제한되어 있다(합산 한도 900만 원 중).", "category": "기본이론"}, {"id": 120, "question": "연금 소득이 1,500만 원을 초과하는 경우 분리 과세 세율은?", "choices": ["5%", "14%", "15%"], "answer": 3, "explanation": "1,500만 원 초과 시 15% 분리 과세와 종합 과세 중 선택할 수 있다.", "category": "기본이론"}, {"id": 121, "question": "생명보험의 계약 관계를 규율하는 법은?", "choices": ["보험 업법", "상법", "민법"], "answer": 2, "explanation": "생명보험의 계약 관계를 규율하는 것은 상법이다.", "category": "기본이론"}, {"id": 122, "question": "표준 약관은 상법에 비해 계약자의 권리를 어떻게 작성하는가?", "choices": ["축소해서 작성", "확대해서 작성", "동일하게 작성"], "answer": 2, "explanation": "표준 약관은 상법보다 계약자의 권리를 확대해서 작성한다.", "category": "기본이론"}, {"id": 123, "question": "변액 보험이 적용받는 법률은?", "choices": ["보험 업법만 적용", "자본시장법만 적용", "보험 업법과 자본시장법 동시 적용"], "answer": 3, "explanation": "변액 보험은 보험 업법과 자본 시장 및 금융 투자에 관한 법률을 동시에 적용받는다.", "category": "기본이론"}, {"id": 124, "question": "상법에서 정하지 않은 사항에 대한 분쟁은 어떤 법에 따라 해결하는가?", "choices": ["보험업법", "민법", "상법 시행령"], "answer": 2, "explanation": "상법에서 정하지 않은 사항으로 다투게 될 경우에는 민법에 따라 해결한다.", "category": "기본이론"}, {"id": 125, "question": "보험 업법에서 정의하는 보험 상품의 지급 대상은?", "choices": ["보험금", "금전 및 그 밖의 급여", "손해배상금"], "answer": 2, "explanation": "보험 상품이란 금전 및 그 밖의 급여를 지급할 것을 약정하는 계약이다.", "category": "기본이론"}, {"id": 126, "question": "생명보험 상품의 정의에서 보험 사고에 해당하는 것은?", "choices": ["우연한 사건", "생존 또는 사망", "상해 또는 질병"], "answer": 2, "explanation": "생명보험 상품은 '생존 또는 사망'에 관하여 급여를 지급하는 것이다.", "category": "기본이론"}, {"id": 127, "question": "보험 업법 시행령에서 생명보험 상품에 포함되는 것은?", "choices": ["상해보험 계약", "생명보험 계약, 연금 보험 계약, 퇴직 보험", "손해보험 계약"], "answer": 2, "explanation": "보험 업법 시행령에서 생명보험 상품은 생명보험 계약, 연금 보험 계약, 퇴직 보험을 포함한다.", "category": "기본이론"}, {"id": 128, "question": "보험 상품 개발 시 기초 서류를 신고하는 기관은?", "choices": ["금융감독원", "금융위원회", "생명보험 협회"], "answer": 2, "explanation": "보험 상품 개발 시 기초 서류를 작성하여 판매 전에 금융위원회에 신고해야 한다.", "category": "기본이론"}, {"id": 129, "question": "약관의 해석에 다툼이 생길 때 적용되는 원칙은?", "choices": ["계약자 불이익의 원칙", "작성자 불이익의 원칙", "보험회사 우선의 원칙"], "answer": 2, "explanation": "약관의 불분명한 조항이나 내용이 있으면 약관 작성자인 보험회사가 불이익을 받는 작성자 불이익의 원칙을 적용한다.", "category": "기본이론"}, {"id": 130, "question": "주계약 없이 특약만 판매하는 것이 가능한가?", "choices": ["가능하다", "불가능하다", "조건부 가능하다"], "answer": 2, "explanation": "특약 없이 주계약만 파는 것은 가능하지만 주계약 없이 특약만 파는 것은 절대 불가능하다.", "category": "기본이론"}, {"id": 131, "question": "제도성 특약에 해당하지 않는 것은?", "choices": ["우량체 할인 특약", "암보장 특약", "연금 전환 특약"], "answer": 2, "explanation": "제도성 특약은 우량체 할인, 선지급 서비스, 연금 전환, 장애인 전용 보험 전환 특약이다. 암보장 특약은 보장을 위한 특약이다.", "category": "기본이론"}, {"id": 132, "question": "보장성 보험과 저축성 보험을 구분하는 기준은?", "choices": ["사망시 보험금", "만기 환급금이 납입 보험료를 초과하는지 여부", "보험 기간"], "answer": 2, "explanation": "만기 환급금이 납입 보험료를 초과하지 않으면 보장성 보험, 초과하면 저축성 보험이다.", "category": "기본이론"}, {"id": 133, "question": "특별 계정에서 운용되는 상품이 아닌 것은?", "choices": ["변액보험", "종신보험", "연금 저축"], "answer": 2, "explanation": "특별 계정 상품은 자산 연계형, 연금 저축, 변액보험, 퇴직 연금이다(자연변퇴). 종신보험은 일반 계정이다.", "category": "기본이론"}, {"id": 134, "question": "금리 확정형 상품의 특징은?", "choices": ["수익성이 높고 안정성이 낮다", "안정성이 높고 수익성이 낮다", "수익성과 안정성 모두 높다"], "answer": 2, "explanation": "금리형 상품은 안정성은 높지만 수익성은 낮은 특징이 있다.", "category": "기본이론"}, {"id": 135, "question": "실적 배당형 상품(변액보험)의 특징은?", "choices": ["안정성이 높다", "성과가 100% 계약자에게 귀속된다", "최저 보증 이율이 설정되어 있다"], "answer": 2, "explanation": "실적 배당형은 성과가 100% 계약자에게 귀속되며 마이너스 수익률도 발생할 수 있다.", "category": "기본이론"}, {"id": 136, "question": "종신보험에 대한 설명으로 틀린 것은?", "choices": ["사망보험이면서 보장성 보험이다", "만기 환급형으로 설계가 가능하다", "특약을 통해 종합 보장형 설계가 가능하다"], "answer": 2, "explanation": "종신보험은 만기가 없는 상품이므로 만기 환급형으로 설계가 불가능하다.", "category": "기본이론"}, {"id": 137, "question": "유니버셜 보험의 특징이 아닌 것은?", "choices": ["추가 납입 기능", "중도 인출 기능", "보험료 면제 기능"], "answer": 3, "explanation": "유니버셜 보험은 추가 납입, 중도 인출, 자유납이 가능하다. 보험료 면제가 아닌 일시적 유예 기능이다.", "category": "기본이론"}, {"id": 138, "question": "장애인이라는 사실만으로 보험 가입을 거절한 경우 처벌은?", "choices": ["1년 이하 징역 또는 1천만 원 이하 벌금", "3년 이하 징역 또는 3천만 원 이하 벌금", "5년 이하 징역 또는 5천만 원 이하 벌금"], "answer": 2, "explanation": "정당한 이유 없이 장애인이라는 이유로 거절하면 3년 이하 징역 또는 3천만 원 이하 벌금이다.", "category": "기본이론"}, {"id": 139, "question": "장애인 전용 보험 중 현재 만들어지지 않은 보험은?", "choices": ["장애인 전용 보장성 보험", "장애인 전용 실손 보험", "장애인 전용 저축성 보험"], "answer": 2, "explanation": "장애인 전용 실손 보험은 현재 만들어지지 않았다.", "category": "기본이론"}, {"id": 140, "question": "장애인 전용 보험이 더 저렴한 이유는?", "choices": ["위험률을 우대 적용하기 때문", "사업 비율과 이자율을 우대 적용하기 때문", "보험금을 줄이기 때문"], "answer": 2, "explanation": "장애인 전용 보험은 사업 비율과 이자율을 우대 적용하기 때문에 저렴하다.", "category": "기본이론"}, {"id": 141, "question": "연금보험을 판매할 수 있는 금융 기관은?", "choices": ["생명보험 회사만", "생명보험 회사와 손해보험 회사", "모든 금융 기관"], "answer": 1, "explanation": "연금보험(저축성 보험)은 생명보험 회사만 판매 가능하다.", "category": "기본이론"}, {"id": 142, "question": "연금 개시 전(1보험 기간)에 사망한 경우 지급되는 보험금은?", "choices": ["납입 보험료 전액", "계약자 적립액 또는 사망 보험금 중 큰 금액", "해약 환급금"], "answer": 2, "explanation": "연금 개시 전 사망 시 계약자 적립액 또는 사망 보험금 중 큰 금액을 지급한다.", "category": "기본이론"}, {"id": 143, "question": "종신 연금형 개시 이후 해지가 가능한가?", "choices": ["가능하다", "불가능하다", "조건부 가능하다"], "answer": 2, "explanation": "종신 연금형은 개시 이후에는 해지를 할 수 없다.", "category": "기본이론"}, {"id": 144, "question": "상속 연금형의 특징은?", "choices": ["생존 기간 동안 원금과 이자를 지급", "생존 기간 동안 적립금의 이자만 지급", "사망 시 이자만 상속"], "answer": 2, "explanation": "상속 연금형은 생존 기간 동안 적립금의 이자만 지급하고 사망 시 원금을 상속인에게 지급한다.", "category": "기본이론"}, {"id": 145, "question": "연금 저축의 납입 기간은?", "choices": ["3년 이상", "5년 이상", "10년 이상"], "answer": 2, "explanation": "연금 저축의 납입 기간은 5년 이상이다.", "category": "기본이론"}, {"id": 146, "question": "연금 저축의 연간 납입 한도는?", "choices": ["1,200만 원", "1,500만 원", "1,800만 원"], "answer": 3, "explanation": "연금 저축의 연간 납입 한도는 1,800만 원이다.", "category": "기본이론"}, {"id": 147, "question": "퇴직 연금의 도입 초기 대상은?", "choices": ["전체 사업장", "5인 이상 사업장", "10인 이상 사업장"], "answer": 2, "explanation": "퇴직 연금 도입 초기에는 5인 이상 사업장을 대상으로 했지만 현재는 전체 사업장으로 확대되었다.", "category": "기본이론"}, {"id": 148, "question": "확정 급여형(DB) 퇴직 연금에서 외부 금융 기관 수익률에 따른 부담을 지는 주체는?", "choices": ["근로자", "사용자(사장님)", "금융 기관"], "answer": 2, "explanation": "확정 급여형은 근로자 퇴직금이 고정되어 있고 사용자 부담이 변동한다.", "category": "기본이론"}, {"id": 149, "question": "기업형 IRP가 적용되는 사업장 규모는?", "choices": ["5인 미만", "10인 미만", "30인 미만"], "answer": 2, "explanation": "기업형 IRP는 상시 근로자 10인 미만 사업장에 적용된다.", "category": "기본이론"}, {"id": 150, "question": "퇴직 연금 판매가 불가능한 금융 기관은?", "choices": ["보험회사", "저축은행", "은행"], "answer": 2, "explanation": "저축은행, 우체국, 국민연금 관리 공단은 퇴직 연금 판매 불가 기관이다.", "category": "기본이론"}, {"id": 151, "question": "변액 보험에서 보험금이 변동하는 기준은?", "choices": ["이율에 따라", "운용 성과에 따라", "보험료에 따라"], "answer": 2, "explanation": "변액 보험은 보험료가 변동하는 것이 아니라 운용 성과에 따라 보험금이 변동한다.", "category": "기본이론"}, {"id": 152, "question": "변액 보험의 펀드 변경 횟수는 연간 최대 몇 회까지 가능한가?", "choices": ["6회", "12회", "24회"], "answer": 2, "explanation": "변액 보험의 펀드는 1년에 최대 12번까지 변경이 가능하다.", "category": "기본이론"}, {"id": 153, "question": "제3보험이 보장하는 위험이 아닌 것은?", "choices": ["상해", "사망", "질병"], "answer": 2, "explanation": "제3보험은 상해, 질병, 간병을 보장한다. 사망은 생명보험의 영역이다.", "category": "기본이론"}, {"id": 154, "question": "제3보험의 주된 보상 방법은?", "choices": ["실손 보상만", "정액 보상과 실손 보상 모두", "정액 보상만"], "answer": 2, "explanation": "제3보험은 정액 보상이 주된 방법이지만 실손 의료 보험은 예외적으로 실손 보상을 사용한다.", "category": "기본이론"}, {"id": 155, "question": "제3보험을 정의하고 있는 법은?", "choices": ["상법", "보험 업법", "민법"], "answer": 2, "explanation": "제3보험은 2003년부터 보험 업법에서 개념을 정의했다. 상법에서는 제3보험을 정의하지 않는다.", "category": "기본이론"}, {"id": 156, "question": "제3보험의 주계약에서 보장 불가능한 것은?", "choices": ["상해 보장", "질병 사망 보장", "질병 보장"], "answer": 2, "explanation": "제3보험 주계약에 질병 사망이 들어가면 생명보험이 되므로 주계약에서는 불가능하다. 특약으로는 가능하다.", "category": "기본이론"}, {"id": 157, "question": "손해보험 회사에서 가입하는 제3보험의 질병 사망 특약 보장 한도는?", "choices": ["보장 기간 최대 80세, 가입 금액 최대 2억", "보장 기간 최대 100세, 가입 금액 제한 없음", "보장 기간 최대 70세, 가입 금액 최대 1억"], "answer": 1, "explanation": "손해보험 회사의 제3보험 질병 사망 특약은 보장 기간 최대 80세, 가입 금액 최대 2억 한도이다.", "category": "기본이론"}, {"id": 158, "question": "제3보험에서 약관상 보장하지 않는 원인으로 사망 시 지급되는 것은?", "choices": ["사망 보험금", "계약자 적립액", "해약 환급금"], "answer": 2, "explanation": "약관상 보장하지 않는 원인으로 사망 시 계약자 적립액을 지급하고 계약이 소멸한다.", "category": "기본이론"}, {"id": 159, "question": "피보험 이익의 개념이 존재하는 보험은?", "choices": ["생명보험", "제3보험", "손해보험"], "answer": 3, "explanation": "피보험 이익은 물건을 담보로 하는 손해보험에만 존재한다. 생명보험과 제3보험에는 없다.", "category": "기본이론"}, {"id": 160, "question": "상해보험의 발생 여부, 발생 시기, 발생 원인, 피해 정도의 확정성은?", "choices": ["모두 확정적이다", "모두 불확정적이다", "발생 시기만 불확정적이다"], "answer": 2, "explanation": "상해보험은 발생 여부, 시기, 원인, 피해 정도가 모두 불확정적이다. 생명보험은 발생 시기만 불확정적이다.", "category": "기본이론"}, {"id": 161, "question": "상해 사고의 세 가지 요건은?", "choices": ["우연성, 급격성, 외래성", "필연성, 점진성, 내부성", "우연성, 계획성, 외래성"], "answer": 1, "explanation": "상해 사고는 우연성, 급격성, 외래성이 동시에 충족되어야 한다.", "category": "기본이론"}, {"id": 162, "question": "상해보험의 보험 기간은?", "choices": ["6개월 이상", "1년 이상", "10년 이상"], "answer": 2, "explanation": "상해보험의 보험 기간은 1년 이상이다.", "category": "기본이론"}, {"id": 163, "question": "상해보험에서 직업의 위험 등급 체계는?", "choices": ["3등급 5단계", "5등급 3단계", "5등급 5단계"], "answer": 2, "explanation": "상해보험은 직업의 구분을 5등급 3단계로 구분하고 있다.", "category": "기본이론"}, {"id": 164, "question": "상해보험 가입 후 직업이 변경된 경우 회사에 알려야 하는 시점은?", "choices": ["1개월 이내", "지체없이", "다음 갱신일에"], "answer": 2, "explanation": "사고 발생 위험이 현저하게 증가한 경우 지체없이 회사에 통보해야 한다.", "category": "기본이론"}, {"id": 165, "question": "질병보험의 보험 기간은?", "choices": ["1년 이상", "5년 이상", "10년 이상"], "answer": 3, "explanation": "질병보험의 보험 기간은 10년 이상이다. 상해보험은 1년 이상이다.", "category": "기본이론"}, {"id": 166, "question": "질병보험의 가입 연령은 일반적으로 몇 세 이상인가?", "choices": ["0세 이상", "15세 이상", "30세 이상"], "answer": 2, "explanation": "질병보험은 대부분 15세 이상부터 가입 가능하다.", "category": "기본이론"}, {"id": 167, "question": "암보험의 면책 기간은 일반적으로 얼마인가?", "choices": ["30일", "60일", "90일"], "answer": 3, "explanation": "일반적으로 보험 회사는 90일이 지난 다음날부터 보장하며, 가입 후 1년 이내는 50%만 지급한다.", "category": "기본이론"}, {"id": 168, "question": "상해보험에 면책 기간 설정이 불필요한 이유는?", "choices": ["보험금이 적어서", "도덕적 해이 가능성이 낮아서", "보험 기간이 짧아서"], "answer": 2, "explanation": "상해보험은 우연하고 급격하고 외래적으로 다치는 것이라 얍삽이(도덕적 해이)가 불가능하므로 면책 기간이 불필요하다.", "category": "기본이론"}, {"id": 169, "question": "암보험에서 동일한 암의 치료 목적으로 2회 이상 입원하는 경우는?", "choices": ["각각 별도의 입원으로 간주", "1회 입원으로 간주", "2회째부터 보장 제외"], "answer": 2, "explanation": "동일한 암의 치료 목적으로 2회 이상 입원하는 경우 1회 입원으로 간주한다.", "category": "기본이론"}, {"id": 170, "question": "실손 의료 보험에서 다수 가입 시 보상 방법은?", "choices": ["중복 보상", "비례 보상", "선착순 보상"], "answer": 2, "explanation": "다수의 실손 의료 보험에 가입한 경우 상법의 실손 보상 원칙에 따라 비례보상한다.", "category": "기본이론"}, {"id": 171, "question": "4세대 실손 보험에서 급여의 자기부담금 비율은?", "choices": ["10%", "20%", "30%"], "answer": 2, "explanation": "급여는 자기부담금 20%, 비급여는 자기부담금 30%이다.", "category": "기본이론"}, {"id": 172, "question": "4세대 실손 보험에서 도수 치료 보장 횟수는?", "choices": ["10회씩 총 30회", "10회씩 총 50회", "20회씩 총 100회"], "answer": 2, "explanation": "도수 치료는 10회씩 총 50회까지 보장되며 10회를 넘길 때마다 의사 소견서가 필요하다.", "category": "기본이론"}, {"id": 173, "question": "비급여 비용 공개를 담당하는 기관은?", "choices": ["생명보험 협회", "건강보험 심사 평가원", "금융감독원"], "answer": 2, "explanation": "비급여 비용은 건강보험 심사 평가원에서 건강 이음 앱을 통해 매년 조사 후 공개한다.", "category": "기본이론"}, {"id": 174, "question": "장기 요양 보험의 등급 판정 신청 대상은?", "choices": ["60세 이상 또는 노인성 질환자", "65세 이상 또는 노인성 질환을 가진 65세 미만자", "70세 이상"], "answer": 2, "explanation": "65세 이상 또는 노인성 질환을 가진 65세 미만자가 등급 판정 신청 대상이다.", "category": "기본이론"}, {"id": 175, "question": "일상생활 장애 상태의 지속 기간 요건은?", "choices": ["30일 이상", "60일 이상", "90일 이상"], "answer": 3, "explanation": "이동하기를 스스로 할 수 없으면서 식사, 화장실, 목욕, 옷입기 중 하나 이상을 할 수 없는 상태가 90일 이상 지속되어야 한다.", "category": "기본이론"}, {"id": 176, "question": "중증 치매 상태의 CDR 검사 결과 기준은?", "choices": ["2점 이상", "3점 이상", "5점 이상"], "answer": 2, "explanation": "CDR 검사 결과가 3점 이상인 경우 중증 치매 상태로 보험금이 지급된다.", "category": "기본이론"}, {"id": 177, "question": "간병보험의 가입 가능 연령은?", "choices": ["15세 이후", "20세 이후", "30세 이후"], "answer": 3, "explanation": "간병보험은 30세 이후부터 가입이 가능하다. 상해는 나이 제한이 적고, 질병은 15세 이상이다.", "category": "기본이론"}, {"id": 178, "question": "간병보험에서 재해로 인한 간병 필요 상태의 면책 기간은?", "choices": ["90일", "면책 기간을 적용하지 않음", "180일"], "answer": 2, "explanation": "재해로 인한 경우 도덕적 해이 가능성이 낮으므로 면책 기간을 적용하지 않는다.", "category": "기본이론"}, {"id": 179, "question": "간병보험에서 위험률 변동 제도를 적용하기 위해 필요한 것은?", "choices": ["생명보험 협회의 승인", "금융위원회의 인가", "금융감독원의 허가"], "answer": 2, "explanation": "간병보험의 위험률 변동 제도는 금융위원회의 인가를 얻어서 보험료를 조정한다.", "category": "기본이론"}, {"id": 180, "question": "생명보험의 보상 방법은?", "choices": ["실손 보상", "정액 보상", "비례 보상"], "answer": 2, "explanation": "사람의 가치는 객관적으로 측정할 수 없으므로 생명보험은 정액 보상 방법을 사용한다.", "category": "기본이론"}, {"id": 181, "question": "보험 나이 계산에서 6개월 이상의 끝수는 어떻게 처리하는가?", "choices": ["버린다", "1년으로 계산한다", "6개월로 계산한다"], "answer": 2, "explanation": "보험 나이는 만 나이를 반올림하여 6개월 미만은 버리고 6개월 이상은 1년으로 계산한다.", "category": "기본이론"}, {"id": 182, "question": "보험료 수금 시 영수증은 누구의 이름으로 교부해야 하는가?", "choices": ["설계사 이름", "회사 이름", "계약자 이름"], "answer": 2, "explanation": "보험료 수금 시 회사 이름의 영수증을 교부해야 한다. 설계사 이름은 오답이다.", "category": "기본이론"}, {"id": 183, "question": "상품 설명서가 제공되는 단계는?", "choices": ["청약 단계", "권유 단계", "관리 단계"], "answer": 2, "explanation": "상품 설명서는 권유 단계에서 제공하는 서류이다.", "category": "기본이론"}, {"id": 184, "question": "변액 보험의 계약 관리 내용 제공 주기는?", "choices": ["연간 1회 이상", "분기별 1회 이상", "월 1회 이상"], "answer": 2, "explanation": "일반 보험은 연간 1회 이상이지만 변액 보험은 분기별 1회 이상 제공해야 한다.", "category": "기본이론"}, {"id": 185, "question": "연성 사기와 경성 사기 중 계획적이고 의도적으로 사고를 발생시키는 것은?", "choices": ["연성 사기", "경성 사기", "둘 다 해당"], "answer": 2, "explanation": "경성 사기는 계획적이고 의도적으로 사고를 발생시키는 행위이다. 연성 사기는 비교적 가벼운 사기이다.", "category": "기본이론"}, {"id": 186, "question": "보험 사기의 특징으로 올바른 것은?", "choices": ["단순화, 개인화 되고 있다", "다양화, 지능화, 조직화 되고 있다", "감소 추세이다"], "answer": 2, "explanation": "보험 사기는 갈수록 다양화, 지능화, 조직화 되어가고 있다.", "category": "기본이론"}, {"id": 187, "question": "퇴직금을 연금으로 10년 이하 수령 시 퇴직 소득세의 몇 %를 과세하는가?", "choices": ["50%", "60%", "70%"], "answer": 3, "explanation": "연금으로 10년 이하 수령 시 퇴직 소득세의 70%, 10년 초과는 60%, 20년 초과는 50%를 과세한다.", "category": "기본이론"}, {"id": 188, "question": "종신형 연금보험의 비과세 연간 수령 한도에서 적용되는 배수는?", "choices": ["2배", "3배", "5배"], "answer": 2, "explanation": "종신형 연금보험의 연간 수령 한도는 전체 평가 금액을 기대 여명으로 나눈 금액의 최대 3배까지 허용된다.", "category": "기본이론"}, {"id": 189, "question": "3세대 실손에서 4세대 실손으로 전환 후 다시 3세대로 돌아갈 수 있는 기간은?", "choices": ["3개월 이내", "6개월 이내", "1년 이내"], "answer": 2, "explanation": "4세대로 전환 후 6개월 이내에 3세대로 돌아갈 수 있으며, 4세대에서 보험금을 받은 이력이 없어야 한다.", "category": "기본이론"}, {"id": 190, "question": "승환 계약에서 설계사 말소 기준 전후 각각의 기간은?", "choices": ["각각 1개월", "각각 3개월", "각각 6개월"], "answer": 2, "explanation": "원래 소속 회사 말소를 기준으로 전후 각각 3개월간 해지된 계약이 6개월 이내에 옮긴 회사에 신계약으로 체결되면 승환 계약이다.", "category": "기본이론"}, {"id": 191, "question": "다음 중 우리나라 생명보험의 역사에 대한 설명으로 틀린 것은?", "choices": ["70년대는 생명보험 산업이 발전을 거듭하면서 보험 시장도 단체보험 위주로 전환되었다", "77년을 보험의 해로 지정하여 보험 산업 근대화 대책을 추진함으로써 생명보험 회사의 자산 규모와 수입보험료가 대폭 증가하였다", "80년대에는 생명보험이 고도 성장을 지속함으로써 자본 시장의 육성에 크게 기여하였다"], "answer": 1, "explanation": "60년대는 단체보험, 70년대는 개인보험 위주로 발전하였으므로 70년대가 단체보험 위주라는 설명은 틀리다.", "category": "예상문제"}, {"id": 192, "question": "다음 중 사회보험과 민영보험에 대한 설명으로 맞는 것은?", "choices": ["사회보험은 개인들이 필요로 하는 수준을 보장한다", "사회보험과 민영보험은 상호 보완과 경쟁이라는 양면성을 가지고 있다", "공영보험은 개인이 만족할 수준을 보장한다"], "answer": 2, "explanation": "사회보험과 민영보험은 상호 보완도 하지만 서로 경쟁도 하는 양면성을 가지고 있다.", "category": "예상문제"}, {"id": 193, "question": "다음 중 생명보험 자산의 운용 원칙에 대한 설명으로 틀린 것은?", "choices": ["안정성: 장래보험금의 지급에 지장이 없도록 안정적으로 운용해야 된다", "유동성: 보험금 지급이 일시에 집중되는 경우를 대비하여 즉시 현금화할 수 있는 부분을 확보해야 된다", "공익성에 관한 설명", "수익성: 적용 이율 이상의 수익률을 넘지 않도록 운영해야 된다"], "answer": 4, "explanation": "수익성은 적용 이율 이상의 수익률로 운영해야 하는 것이지, 넘지 않도록 운영하는 것이 아니다.", "category": "예상문제"}, {"id": 194, "question": "다음 중 노후보장 체계와 의료비 지출에 대한 설명으로 틀린 것은?", "choices": ["국민 연금은 도입 당시 명목 소득 대체율이 70%였으나 세 차례 연금 개혁으로 43% 수준으로 하향 조정될 예정이다", "국민 건강보험의 보장률이 65% 수준에서 정체되어 있다", "베이비붐 세대의 은퇴와 함께 지속적으로 노후 대비에 힘쓴 결과 65세 이상 은퇴 연령층의 상대적 빈곤율은 OECD 국가 평균 수준으로 개선되었다"], "answer": 3, "explanation": "65세 이상 은퇴 연령층의 상대적 빈곤율은 OECD 국가 중 최하위 수준이며, 개선되지 않았다.", "category": "예상문제"}, {"id": 195, "question": "다음 중 생명보험을 필요로 하는 사회적 배경으로 틀린 것은?", "choices": ["급속한 고령화와 노후 준비가 부족하다", "1인 가구 증가와 자기 책임주의가 강화된다", "정보 사회 발전에 따라 고객들의 다양한 정보를 확보하는 것이 가능해지면서 보험 설계사의 역할이 축소되었다"], "answer": 3, "explanation": "정보가 많아질수록 이를 정리해 줄 수 있는 보험 설계사의 역할이 더욱 중요해지고 있으므로 축소가 아니라 확대되어야 한다.", "category": "예상문제"}, {"id": 196, "question": "다음 중 재무 설계의 필요성과 향후 전망에 대한 설명으로 틀린 것은?", "choices": ["현재 우리나라의 가계 자산 중 금융자산이 차지하는 비중이 낮은 수준이다", "2009년 자본 시장과 금융 투자에 관한 법률이 시행됨에 따라 새로운 금융 상품의 출시는 줄어들고 있다", "선진국처럼 현금화가 쉬운 금융자산의 비중이 점차 늘어날 것으로 예상된다"], "answer": 2, "explanation": "자본 시장과 금융 투자에 관한 법률은 금융 기관들의 업무 제한 규제를 줄여주는 법이므로 다양한 금융 상품이 늘어나고 있다.", "category": "예상문제"}, {"id": 197, "question": "다음 표에서 설명하는 생명보험의 기본 원리는 무엇인가? '어떤 사건의 발생률을 1회나 2회의 관찰로는 예측이 어렵지만 관찰 횟수를 늘리면 일정한 수치에 가까워진다.'", "choices": ["수지상등의 원칙", "대수의 법칙", "이득 금지의 원칙"], "answer": 2, "explanation": "관찰 횟수를 늘리면 일정한 수치에 가까워진다는 것은 큰 수의 법칙인 대수의 법칙에 해당한다.", "category": "예상문제"}, {"id": 198, "question": "다음 중 피보험자에 대한 설명으로 틀린 것은?", "choices": ["피보험자는 보험 사고의 대상이 되는 자이다", "피보험자는 1인만 지정할 수 있다", "계약자와 피보험자가 동일하지 않을 수 있다"], "answer": 2, "explanation": "피보험자, 계약자, 수익자 등 계약 관계자들은 모두 다수가 가능하므로 1인만 지정할 수 있다는 것은 틀리다.", "category": "예상문제"}, {"id": 199, "question": "다음 보기에 괄호에 들어갈 말로 알맞은 것은? '보험 기간보다 짧은 기간 동안 보험료를 납입하는 것을 ( )이라 한다.'", "choices": ["일시납", "단기납", "전기납"], "answer": 2, "explanation": "보험 기간보다 짧은 기간 동안 보험료를 납입하는 것을 단기납이라고 한다.", "category": "예상문제"}, {"id": 200, "question": "다음 중 보험료 산출 방식인 현금 흐름 방식에 대한 설명으로 맞는 것은?", "choices": ["보수적 기초율을 가정한다", "산출 방법이 복잡하고 전산 시스템 관련 비용이 많이 든다", "정교한 보험료 산출이 불가능하다"], "answer": 2, "explanation": "현금 흐름 방식은 다양한 기초율을 적용하여 정교한 보험료 산출이 가능하지만, 산출 방법이 복잡하고 전산 시스템 비용이 많이 발생한다.", "category": "예상문제"}, {"id": 201, "question": "다음 보기에서 보험 계약에서 청약 철회가 가능한 날짜는? (일반 계약, 청약일: 10월 3일, 보험증권 수령일: 10월 8일)", "choices": ["10월 21일", "10월 25일", "11월 2일"], "answer": 1, "explanation": "청약 철회는 증권을 받은 날로부터 15일 이내가 원칙이므로 10월 8일 + 15일 = 10월 23일 이내여야 하며, 보기 중 가능한 날짜는 10월 21일이다.", "category": "예상문제"}, {"id": 202, "question": "다음 중 청약 철회에 대한 설명으로 적절하지 않은 것은?", "choices": ["보험료를 신용 카드로 납입한 경우에 회사가 청약 철회 접수일로부터 3영업일 이내에 대금 청구가 되지 않도록 하면 보험료를 반환한 것으로 본다", "보험 증권을 받은 날에 대한 다툼이 있는 경우에는 계약자가 증명해야 된다", "보험 회사의 납입 보험료 반환이 늦어질 경우 보험 계약 대출 이율을 연단위 복리로 계산한 금액을 더하여 지급한다"], "answer": 2, "explanation": "보험 증권을 받은 날에 대한 다툼이 있는 경우에는 계약자가 아닌 보험회사가 증명해야 한다.", "category": "예상문제"}, {"id": 203, "question": "다음 중 보험 계약의 효력 발생 요건이 충족되지 않는 계약은?", "choices": ["1회 보험료 납입을 위해 신용카드 매출 승인에 필요한 정보를 제공한 계약", "진단 계약에서 진단을 받고 회사에 승낙이 있었으나 아직 보험료를 납입하지 않은 계약", "청약 이후에 건강진단을 받고 1회 보험료를 납입한 후에 회사의 승낙을 기다리고 있는 계약"], "answer": 2, "explanation": "진단 계약의 경우 1회 보험료 납입과 진단이 모두 완료되어야 효력이 발생하므로, 보험료를 아직 납입하지 않은 계약은 효력이 발생하지 않는다.", "category": "예상문제"}, {"id": 204, "question": "무진단 보험에서 효력이 발생한 날로 알맞은 것은?", "choices": ["청약일", "1회 보험료 납입일", "승낙일"], "answer": 2, "explanation": "무진단 계약은 1회 보험료 납입을 효력 발생 요건으로 하므로 1회 보험료를 납입한 날이 효력 발생일이다.", "category": "예상문제"}, {"id": 205, "question": "다음 중 보험 계약의 무효에 대한 설명으로 틀린 것은?", "choices": ["무효란 청약한 때로부터 효력이 발생하지 않는 것이다", "계약이 무효가 되면 회사는 이미 납입한 보험료를 계약자에게 반환해야 된다", "회사의 귀책 사유가 있을 경우에는 보험 계약 대출 이율을 연단위 복리로 계산한 금액을 더하여 반환한다"], "answer": 1, "explanation": "무효란 청약한 때가 아니라 계약이 성립한 때(회사가 승낙한 날)로부터 효력이 발생하지 않는 것을 말한다.", "category": "예상문제"}, {"id": 206, "question": "다음 중 보험회사의 의무에 해당하지 않는 것은?", "choices": ["보험금 지급 의무", "약관 교부 및 설명 의무", "보험금 지급 사유가 발생한 경우에 알려주는 통지 의무"], "answer": 3, "explanation": "통지 의무는 보험회사의 의무가 아니라 계약자, 피보험자 또는 수익자의 의무이다.", "category": "예상문제"}, {"id": 207, "question": "다음 중 사망의 추정 및 인정 제도에 대한 설명으로 틀린 것은?", "choices": ["일반 실종은 3년간 생사가 분명하지 않으면 법원에 실종 선고를 청구할 수 있다", "특별 실종은 1년간 생사가 분명하지 않으면 법원에 실종 선고를 청구할 수 있다", "인정 사망은 지체 없이 관공서에 신고한다"], "answer": 1, "explanation": "일반 실종은 3년이 아니라 5년간 생사가 분명하지 않은 경우에 법원에 실종 선고를 청구할 수 있다.", "category": "예상문제"}, {"id": 208, "question": "다음 중 보험금 지급기한에 대한 설명으로 틀린 것은?", "choices": ["청구와 관련된 서류가 접수되는 경우에는 7영업일 이내에 지급한다", "조사가 필요한 경우에는 10영업일 이내에 지급한다", "10영업일 이내에 지급하지 못할 것으로 예상되는 경우에는 30영업일까지 가능하다"], "answer": 1, "explanation": "청구 관련 서류가 접수되는 경우 기본 지급 기한은 7영업일이 아니라 3영업일 이내이다.", "category": "예상문제"}, {"id": 209, "question": "다음 중 계약전 알릴 의무에 대한 설명으로 틀린 것은?", "choices": ["피보험자의 현재 및 과거의 질병과 직업, 운전 여부 등을 알려야 된다", "청약서 질문표에 명시된 사항은 제외한다", "계약자 또는 피보험자는 청약서에 질문한 사항에 대하여 반드시 사실대로 알려야 된다"], "answer": 2, "explanation": "청약서 질문표에 명시된 사항은 회사가 가장 궁금해하는 내용으로 반드시 알려야 하며, 제외해서는 안 된다.", "category": "예상문제"}, {"id": 210, "question": "다음 보기에 괄호에 들어갈 말로 알맞은 것은? '회사는 계약자 또는 ( )의 고의 또는 ( )으로 중요한 사항에 대하여 사실과 다르게 알린 경우'", "choices": ["수익자, 경과실", "피보험자, 중대한 과실", "수익자, 중대한 과실"], "answer": 2, "explanation": "알릴 의무는 계약자와 피보험자에게 있으며, 고의 또는 중대한 과실로 알릴 의무를 위반한 경우에 해당한다.", "category": "예상문제"}, {"id": 211, "question": "다음 중 보험 계약의 부활에 대한 설명으로 틀린 것은?", "choices": ["회사가 부활을 승낙하면 계약자는 부활을 청약한 날까지 연체된 보험료에 평균 공시 이율의 1% 범위 내에서 계산한 금액을 더하여 납입해야 된다", "금리 연동형 계약은 부활할 수 없다", "보험 계약을 부활하는 경우 신계약과 동일한 절차를 거친다"], "answer": 2, "explanation": "금리 연동형 계약도 부활이 가능하다. 부활이 안 되는 경우는 해약 환급금을 이미 받았거나 해지된 날로부터 3년이 지난 경우이다.", "category": "예상문제"}, {"id": 212, "question": "다음 보기에 괄호에 들어갈 알맞은 용어는 무엇인가? '보험 계약의 부활은 ( ) 가능성이 높기 때문에 이를 방지하기 위해 신계약 절차와 동일하게 이루어진다.'", "choices": ["도덕적 해이", "보험 사기", "역선택"], "answer": 3, "explanation": "보험 계약의 부활은 역선택 가능성이 높기 때문에 이를 방지하기 위해 신계약과 동일한 절차를 거친다.", "category": "예상문제"}, {"id": 213, "question": "다음 보기에 괄호 안에 들어갈 내용으로 알맞은 것은? '부당한 계약 전환시 보험 계약자는 기존 보험 계약이 소멸한 날로부터 ( ) 이내에 소멸 계약의 부활을 청구하고 새로운 계약을 취소할 수 있다.'", "choices": ["3개월", "1년", "6개월"], "answer": 3, "explanation": "부당한 계약 전환시 보험 계약자는 기존 보험 계약이 소멸한 날로부터 6개월 이내에 부활을 청구할 수 있다.", "category": "예상문제"}, {"id": 214, "question": "다음 중 소멸 시효의 기한이 다른 것은?", "choices": ["보험료 청구권", "보험금 청구권", "보험료 반환 청구권"], "answer": 1, "explanation": "보험료 청구권은 2년이고, 보험금 청구권과 반환 청구권은 3년이므로 보험료 청구권만 기한이 다르다.", "category": "예상문제"}, {"id": 215, "question": "다음 중 보험 계약 대출 및 상환에 대한 설명으로 틀린 것은?", "choices": ["계약자는 보험 계약 대출금과 보험 계약 대출이자를 언제든지 상환할 수 있다", "계약자는 가입한 보험의 만기 환급금 범위 내에서 회사가 정한 방법에 따라 보험 계약 대출을 받을 수 있다", "순수 보장성 보험 등 보험 상품의 종류에 따라 보험 계약 대출이 제한될 수 있다"], "answer": 2, "explanation": "보험 계약 대출은 만기 환급금이 아닌 해약 환급금 범위 내에서 받을 수 있다.", "category": "예상문제"}, {"id": 216, "question": "다음 중 언더라이팅 업무에 해당하지 않는 것을 고르시오.", "choices": ["보험료나 보험금의 한도를 결정하는 것", "보험금 청구", "우량 피보험자를 선택하는 것"], "answer": 2, "explanation": "보험금 청구는 클레임 업무에 해당하며, 언더라이팅 업무가 아니다.", "category": "예상문제"}, {"id": 217, "question": "다음 중 언더라이터의 업무 수칙에 해당하지 않는 것을 고르시오.", "choices": ["보험 고유의 성질인 다수의 동일 피보험체 위험 집단을 구성하는 역할을 수행한다", "전문화된 지식과 실무 능력을 갖추도록 노력하며 보험 전문가로서 자리매김할 수 있도록 지속적인 자기 개발을 한다", "직무상 취득한 개인 정보를 다양한 목적으로 활용하여 계약자의 이익 향상을 위하여 노력한다"], "answer": 3, "explanation": "고객의 개인 정보는 계약 인수 용도로만 활용해야 하며, 다양한 목적으로 활용하면 불법이다.", "category": "예상문제"}, {"id": 218, "question": "다음 중 언더라이팅 단계 중에서 3차 선택에 대한 설명으로 알맞은 것은?", "choices": ["피보험자의 위험을 평가하여 계약의 조건 등을 최종 결정하는 언더라이터에 의한 선택을 한다", "신체적, 환경적, 도덕적 위험에 대해 직접 조사한다", "피보험자의 건강 상태나 직업 등을 청약서에 사실대로 기재하게 한다"], "answer": 1, "explanation": "3차 언더라이팅은 언더라이터가 피보험자의 위험을 평가하여 계약 조건 등을 최종 결정하는 단계이다.", "category": "예상문제"}, {"id": 219, "question": "다음 중 체감성 위험에 적용하는 보험 계약 인수 방법은 무엇인가?", "choices": ["보험금 삭감", "보험료 할증", "보험 기간 제한"], "answer": 1, "explanation": "체감성 위험(위험이 점차 감소)의 경우 보험금을 적게 주는 보험금 삭감 방법으로 인수한다.", "category": "예상문제"}, {"id": 220, "question": "다음 중 클레임 업무에 해당하지 않는 것은?", "choices": ["보험금 청구", "사고의 조사", "진단 항목의 추가"], "answer": 3, "explanation": "진단 항목의 추가는 언더라이팅 업무에 해당하며, 클레임 업무가 아니다.", "category": "예상문제"}, {"id": 221, "question": "다음 중 부당 영업 행위에 해당하는 것은?", "choices": ["보험 소비자의 구매 의사에 맞는 보험 상품을 권유 또는 판매하는 행위", "기존 보험 상품의 계약 만료에 따라서 새로운 보험 상품을 권유 또는 판매하는 행위", "보험 소비자를 대신하여 서명하는 행위"], "answer": 3, "explanation": "보험 소비자를 대신하여 서명하는 행위는 명백한 불법 행위이자 부당 영업 행위이다.", "category": "예상문제"}, {"id": 222, "question": "다음 중 계약 체결 및 유지 단계의 정보 제공에 대한 설명으로 틀린 것은?", "choices": ["제공하는 안내 자료의 예상 수치가 실제 적용되는 이율이나 수익률 등에 따라 달라질 수 있다는 점을 설명해야 된다", "저축성 보험의 경우 판매 시점의 기준 이율을 적용한 경과 기간별 해약 환급금을 안내해야 된다", "미가입 시 과태료 부과 등 행정 조치가 취해지는 의무보험에 대해서는 보험 기간이 만료되기 일정 기간 이전에 보험 계약자에게 안내해야 한다"], "answer": 2, "explanation": "저축성 보험의 경우 기준 이율이 아닌 공시 이율을 적용한 경과 기간별 해약 환급금을 안내해야 한다.", "category": "예상문제"}, {"id": 223, "question": "모집 종사자를 위촉하기 전에 확인해야 할 절차로 가장 적절한 것은?", "choices": ["이클린 보험 서비스를 이용해 해당 종사자의 모집 경력 등을 확인한다", "최근 민원이 많이 접수된 모집사를 우선적으로 위촉 대상으로 고려한다", "생명보험 협회 기타 공시실에서 모집 종사자의 보험법 위반 경력 등을 확인한다"], "answer": 1, "explanation": "생명보험 협회에서 제공하는 이클린 보험 서비스를 통해 해당 종사자의 모집 경력 등을 확인할 수 있다.", "category": "예상문제"}, {"id": 224, "question": "다음 중 분쟁 방지 및 민원 처리에 대한 설명으로 옳은 것은?", "choices": ["보험회사는 모집 종사자의 편의를 위해 독립적이고 공정한 민원 처리와 구제 절차를 마련해야 한다", "보험회사는 이원화된 민원 접수 채널을 운영하고 빠른 민원 해결이 가능한 전산 시스템을 구축해야 된다", "보험회사는 분쟁 발생 시 소비자에게 분쟁 해결에 관한 내부 절차를 알려야 하며 보험 소비자가 분쟁 처리 결과에 이의가 있는 경우에는 이의 제기 방법에 대해 안내해야 한다"], "answer": 3, "explanation": "보험회사는 분쟁 발생 시 소비자에게 절차를 알리고 이의 제기 방법을 안내해야 한다.", "category": "예상문제"}, {"id": 225, "question": "다음 중 보험 설계사의 책임에 대한 내용으로 틀린 것은?", "choices": ["계약자가 보험 계약을 청약하는 경우 보험 약관을 교부하고 약관의 내용을 직접 설명해야 된다", "계약자 보관용 청약서 부본을 계약자에게 제공하여야 한다", "고객을 소개받은 경우에 소개인에게 금전이나 물품 등의 대가를 지급할 수 있다"], "answer": 3, "explanation": "고객을 소개받은 경우 소개인에게 금전이나 물품 등의 대가를 지급하는 것은 금지 행위이다.", "category": "예상문제"}, {"id": 226, "question": "다음 중 보험 범죄에 대한 내용으로 틀린 것을 고르시오.", "choices": ["보험사기는 연성 사기와 경성 사기로 구분된다", "보험 사기 방지 특별법상 보험 사기 행위는 형법상 사기죄와 처벌 수준이 동일하다", "보험 계약 당시에 보험료를 절감하기 위해 보험 회사에 허위 정보를 제공하는 것은 연성 사기에 해당한다"], "answer": 2, "explanation": "보험사기 방지 특별법은 형법상 사기죄보다 처벌이 강화되어 있다(벌금 2천만 원에서 5천만 원으로 상향).", "category": "예상문제"}, {"id": 227, "question": "다음 중 보험사기 유형이 다른 것은?", "choices": ["보험금을 노리고 피보험자의 신체에 상해를 입히는 행위", "제3자를 통한 대리진단으로 다수의 보험을 가입하는 행위", "이미 사망한 사람을 피보험자로 보험에 가입한 행위"], "answer": 1, "explanation": "1번은 직접적 피해자가 발생하는 경성 사기이고, 2번과 3번은 가입 시 거짓말을 하는 연성 사기에 해당한다.", "category": "예상문제"}, {"id": 228, "question": "다음 중 보험 사기와 보험 범죄의 차이에 대한 설명으로 틀린 것은?", "choices": ["보험 범죄는 보험 제도를 역이용하여 고의적으로 보험금을 편취하는 불법 행위이다", "보험 사기는 보험 회사를 기망하여 보험금을 청구하는 행위 또는 사기죄가 성립되는 행위이다", "보험 범죄는 보험 사기보다 광범위한 개념을 가진다"], "answer": 3, "explanation": "보험 사기가 더 큰(광범위한) 개념이고, 보험 범죄는 보험 사기 중 더 악질적인 것들을 법률로 처벌하는 더 작은 개념이다.", "category": "예상문제"}, {"id": 229, "question": "다음 중 보험사기 방지 활동에 대한 설명으로 틀린 것은?", "choices": ["경찰청은 보험사기 수사 역량 강화를 위해 전문 수사 팀을 지정하고 있다", "보험 범죄 수사 협의회에는 금융 감독원과 보험 협회가 있다", "금융 감독원에서는 보험 범죄 신고 센터를 설치하여 운영하고 있다"], "answer": 2, "explanation": "보험 범죄 수사 협의회에는 경찰이 반드시 포함되어야 하는데, 경찰이 빠져 있어 틀린 설명이다.", "category": "예상문제"}, {"id": 230, "question": "다음 중 생명보험 업계의 자살 예방 프로그램에 해당하지 않는 것은?", "choices": ["한강 다리에 설치된 SOS 생명의 전화", "청소년 자살 예방 어플 '다 들어 줄게, 힐링 톡톡'", "보고 듣고 말하기 게이트 키퍼"], "answer": 3, "explanation": "'보고 듣고 말하기'와 '게이트 키퍼'는 정부에서 운영하는 프로그램이며, 생명보험 업계의 프로그램이 아니다.", "category": "예상문제"}, {"id": 231, "question": "다음 중 보험을 모집할 수 있는 자에 대한 설명으로 맞는 것은?", "choices": ["설계사는 계약의 체결을 대리하는 자이다", "대리점은 보험회사에 소속되어 중개하는 자이다", "중개사는 독립적으로 중개하는 자이다"], "answer": 3, "explanation": "설계사는 중개하는 자, 대리점은 대리하는 자, 중개사는 독립적으로 중개하는 자이다.", "category": "예상문제"}, {"id": 232, "question": "다음 중 보험 설계사가 되지 못하는 자에 해당하지 않는 경우는?", "choices": ["보험법에 따라 벌금 이상의 형을 선고받고 그 집행이 끝나지 않은 자", "보험법에 따라 금고 이상의 형의 집행유예를 선고받고 그 유예 기간 중에 있는 자", "도로교통법에 따라 벌금 이상의 형을 받고 면제된 날로부터 2년이 지나지 아니한 자"], "answer": 3, "explanation": "도로교통법은 보험과 무관한 법률이므로, 이에 따른 처벌은 설계사 등록 결격 사유에 해당하지 않는다.", "category": "예상문제"}, {"id": 233, "question": "다음 중 교차 모집 제도에 대한 설명으로 틀린 것은?", "choices": ["소속 보험회사 외에 한 개의 다른 종류의 보험회사 상품을 판매할 수 있는 것을 교차 모집 제도라고 한다", "생명보험 회사에 속한 소속 보험 설계사가 한 개의 다른 종류인 제3보험을 전업으로 영위하는 보험 회사를 위하여 모집하는 경우도 해당된다", "보험 계약의 종류에 따라 등록 요건을 갖추어서 설계사를 등록해 주는 곳은 금융 감독원이다"], "answer": 3, "explanation": "설계사 등록은 금융 감독원이 아닌 보험 협회에서 담당한다.", "category": "예상문제"}, {"id": 234, "question": "다음 중 보험 설계사의 등록과 말소에 대한 설명으로 틀린 것은?", "choices": ["보험 설계사 등록은 신규 등록과 경력 등록으로 구분된다", "보험 설계사의 말소는 보험 회사를 통하거나 본인이 직접 보험 협회에 신청할 수 있다", "소속 보험 회사를 거치지 않고 보험 협회에 직접 등록 신청을 할 수 있다"], "answer": 3, "explanation": "설계사 등록은 보험회사를 통해서만 협회에 신청할 수 있으며, 직접 등록 신청은 불가능하다.", "category": "예상문제"}, {"id": 235, "question": "보험 설계사의 영업 정지 또는 등록 취소에 대한 설명으로 틀린 것은?", "choices": ["보험법에 따라 설계사가 되지 못하는 자에 해당하는 경우에는 등록이 취소된다", "금융 감독원은 보험 설계사에 대한 영업 정지 및 등록 취소를 명하려면 설계사에 대하여 청문을 하여야 한다", "1년 이상 정당한 사유 없이 영업을 하지 않는 경우에는 등록이 취소될 수 있다"], "answer": 2, "explanation": "청문의 권한은 금융 감독원이 아닌 금융위원회에 있다.", "category": "예상문제"}, {"id": 236, "question": "다음 중 생명보험 대리점 등록에 대한 설명으로 틀린 것은?", "choices": ["소속 임직원 200명 중 생명보험 설계사 등록 요건을 갖춘 임직원이 30명인 법인은 법인보험 대리점 등록이 가능하다", "생명보험 대리점을 신규로 등록하고자 하는 개인은 생명보험 협회에서 실시하는 대리점 자격 시험에 합격해야 된다", "신청일로부터 4년 이내에 2년 이상 생명보험 관계 업무에 종사한 경력이 있고 교육을 이수한 사람은 대리점 등록이 가능하다"], "answer": 2, "explanation": "대리점 자격 시험은 생명보험 협회가 아닌 보험연수원에서 주관하고 있다.", "category": "예상문제"}, {"id": 237, "question": "다음 중 금융 기관 보험 대리점이 모집할 수 있는 금융 상품으로 바르게 짝지어진 것은?", "choices": ["개인 연금 보험, 사망 특약이 포함된 상품", "개인 연금 보험, 연금 보험, 생사혼합보험", "신형 생명 보험, 종신보험"], "answer": 2, "explanation": "금융 기관 보험 대리점에서는 보장성 보험, 자동차 보험, 질병 사망 특약이 포함된 상품은 판매할 수 없다.", "category": "예상문제"}, {"id": 238, "question": "다음 중 금융 기관 보험 대리점에 대한 설명으로 틀린 것은?", "choices": ["보험회사에 대한 대리점 자격으로 보험 계약의 체결을 대리한다", "인터넷 홈페이지를 이용하여 불특정 다수의 고객을 대상으로 보험 상품을 안내 또는 설명하여 모집할 수 있다", "모든 금융 기관 보험 대리점은 전화, 우편, 컴퓨터 등으로 모집이 가능하다"], "answer": 3, "explanation": "전화, 우편, 컴퓨터 등으로 모집이 가능한 것은 신용 카드업자만 해당된다.", "category": "예상문제"}, {"id": 239, "question": "다음 보기에 괄호에 들어갈 단어가 알맞게 연결된 것은? '특별 이익의 제공 금지에 따라서 처벌 대상이 되는 금품은 최초 1년간 납입되는 보험료의 ( )와 ( ) 중에 적은 금액을 초과하는 금품을 말한다.'", "choices": ["10%, 3만 원", "20%, 5만 원", "15%, 10만 원"], "answer": 1, "explanation": "특별 이익 제공 금지 기준은 최초 1년간 납입되는 보험료의 10%와 3만 원 중 적은 금액까지이다.", "category": "예상문제"}, {"id": 240, "question": "다음 표에서 설명하는 내용은 무엇인가? '계약자의 청약이 없음에도 계약자 또는 피보험자의 명의를 가명 또는 차명으로 하여 모집 종사자가 청약서를 임의로 작성하여 성립시키는 계약'", "choices": ["작성 계약", "승환 계약", "부당 계약"], "answer": 1, "explanation": "계약자의 청약 없이 모집 종사자가 가명이나 차명으로 청약서를 임의 작성하여 성립시키는 계약을 작성 계약이라 한다.", "category": "예상문제"}, {"id": 241, "question": "다음 승환 계약에 대한 설명에서 괄호 안에 들어갈 내용이 순서대로 알맞은 것은? '말소 전후 각 ( )간 소멸된 계약이 말소일로부터 ( ) 이내에 신계약으로 체결'", "choices": ["6개월, 1년", "3개월, 6개월", "1개월, 3개월"], "answer": 2, "explanation": "승환 계약의 기준은 말소 전후 각 3개월간 소멸된 계약이 말소일로부터 6개월 이내에 신계약으로 체결되는 것이다.", "category": "예상문제"}, {"id": 242, "question": "다음 중 우수인증 설계사의 인증 기준에 대한 설명으로 틀린 것은?", "choices": ["보험 계약 유지율은 13회차 90%, 25회차 80% 이상이어야 한다", "우수인증 설계사로 인증을 받기 위해서는 엄격한 금융 감독원의 인증 기준을 충족해야 된다", "직전 1년간 민원해지는 없어야 된다"], "answer": 2, "explanation": "우수인증 설계사에 대한 인증은 금융 감독원이 아닌 생명보험 협회에서 담당하고 있다.", "category": "예상문제"}, {"id": 243, "question": "다음 중 우수인증 설계사의 인증 기준으로 맞는 것은?", "choices": ["동일 회사에서 1년 이상 재직 중인 전속 설계사", "보험 계약 유지율은 13회차 90%, 25회차 80% 이상인 설계사", "직전 3년간 보험법에 따른 불완전 판매 행위가 없는 설계사"], "answer": 3, "explanation": "1번은 1년이 아닌 3년 이상 재직이 기준이고, 3번이 맞는 기준이다.", "category": "예상문제"}, {"id": 244, "question": "다음 중 우수인증 설계사 제도에 대한 설명으로 틀린 것은?", "choices": ["우수인증 설계사 인증 기간은 3년이다", "보험 계약자에게 양질의 서비스를 제공하기 위한 기반이 된다", "2008년부터 우수인증 설계사 제도를 도입하여 운영하고 있다"], "answer": 1, "explanation": "인증 기간은 3년이 아니라 1년 단위이다.", "category": "예상문제"}, {"id": 245, "question": "다음 중 예금자 보호법에 의해 보호되는 보험 계약에 해당하는 것은?", "choices": ["변액 보험의 특약", "보험료 납부자가 법인인 보험 계약", "재보험"], "answer": 1, "explanation": "변액 보험은 주계약은 예금자 보호가 안 되지만 특약은 예금자 보호가 된다.", "category": "예상문제"}, {"id": 246, "question": "다음 중 금융분쟁 조정 위원회에 대한 설명으로 맞는 것은?", "choices": ["금융위원회에서 운영한다", "당사자가 조정안을 제시받은 날로부터 20일 내에 수락하지 않은 경우에는 수락한 것으로 본다", "조정의 회부를 받은 때에는 60일 이내에 심의하여 조정안을 작성해서 양당사자에게 제시하여야 한다"], "answer": 3, "explanation": "금융분쟁 조정 위원회는 금융 감독원에서 운영하며, 회부 후 60일 이내에 조정안을 작성하여 제시해야 한다.", "category": "예상문제"}, {"id": 247, "question": "다음 중 보험금 대리 청구인 지정 제도에 대한 설명으로 틀린 것은?", "choices": ["계약자, 피보험자, 수익자가 동일한 보험 상품인 경우에 사용 가능한 제도이다", "대리 청구인 지정은 수익자를 바꾸는 것이 아니고 청구만 대신해 주는 것이다", "지정 범위는 계약자의 주민 등록 배우자 또는 4촌 이내의 친족까지 지정이 가능하다"], "answer": 3, "explanation": "대리 청구인 지정 범위는 4촌 이내가 아니라 3촌 이내의 친족까지 가능하다.", "category": "예상문제"}, {"id": 248, "question": "다음 표에서 김생명 씨의 대리 청구인 지정 가능한 사람은 몇 명인가?", "choices": ["1명", "2명", "3명"], "answer": 2, "explanation": "사실혼 배우자는 주민등록 배우자가 아니므로 불가, 어머니(1촌)와 친누나(2촌)만 가능하여 총 2명이다.", "category": "예상문제"}, {"id": 249, "question": "다음 중 생명보험 광고 심의 제도의 근거 법률로 맞는 것은?", "choices": ["보험업법", "금융 소비자 보호에 관한 법률", "상법"], "answer": 2, "explanation": "광고 심의 제도의 근거 법률은 금융 소비자 보호에 관한 법률(금소법)이다.", "category": "예상문제"}, {"id": 250, "question": "다음 중 보험회사의 업무 광고에 해당하지 않는 것은?", "choices": ["특정 보험 상품을 소개하는 상품 광고", "재무 설계 광고", "헬스케어 서비스 광고"], "answer": 1, "explanation": "업무 광고는 재무 설계 광고와 헬스케어 서비스 광고 두 가지이며, 상품 광고는 업무 광고가 아니다.", "category": "예상문제"}, {"id": 251, "question": "다음 중 경영 공시에 대한 설명으로 맞는 것은?", "choices": ["정기 공시에는 1년 단위로 하는 결산 공시와 3개월 또는 6개월 단위로 하는 분기별 임시 결산 공시가 있다", "분기별 임시 결산 공시는 분기 결산일로부터 3개월 이내에 공시한다", "결산 공시는 매 영업연도 결산일로부터 6개월 이내에 공시한다"], "answer": 1, "explanation": "정기 공시에는 결산 공시와 분기별 임시 결산 공시가 있다.", "category": "예상문제"}, {"id": 252, "question": "다음 중 경영 공시의 수시 공시 항목에 해당하지 않는 것을 고르시오.", "choices": ["주요 조치 사항", "중대한 변경", "재무제표"], "answer": 3, "explanation": "재무제표는 정기 공시 항목이며, 수시 공시 항목이 아니다.", "category": "예상문제"}, {"id": 253, "question": "다음 중 보험 정보 공시에 대한 설명으로 틀린 것은?", "choices": ["보험회사는 회사 홈페이지에 경영 공시를 해야 한다", "보험회사는 자사 인터넷 홈페이지 내에 상품 공시를 이용하여 보험 상품 관련 자료 등을 공시하고 있다", "보험회사는 회사 홈페이지를 통하여 상품 비교 공시를 해야 한다"], "answer": 3, "explanation": "상품 비교 공시는 회사가 아닌 협회에서 하는 공시 항목이다.", "category": "예상문제"}, {"id": 254, "question": "다음 중 온라인 보험 슈퍼마켓 '보험다모아'의 주관 기관으로 맞는 것은?", "choices": ["금융 감독원", "생명보험 협회와 손해보험 협회 공동 운영", "금융위원회"], "answer": 2, "explanation": "보험다모아는 생명보험 협회와 손해보험 협회가 공동으로 운영한다.", "category": "예상문제"}, {"id": 255, "question": "다음 중 보험민원에 관한 내용으로 틀린 것은?", "choices": ["금융 민원 중 보험업권의 비율이 가장 높다", "상품 기획 단계부터 민원 발생 소지의 가능성을 염두해 두고 상품을 개발해야 된다", "악성민원인의 무리한 민원 요구를 수용한다면 동일 유형의 민원은 제기되지 않는다"], "answer": 3, "explanation": "악성 민원인의 무리한 요구를 수용하면 더 큰 요구를 하게 된다.", "category": "예상문제"}, {"id": 256, "question": "다음 중 보험 민원에 대한 설명으로 틀린 것은?", "choices": ["금융 민원 중 보험 민원의 비중이 가장 높다", "보험 민원은 직접 방문으로만 접수할 수 있다", "금융 감독원에 보험민원을 신청할 수 있다"], "answer": 2, "explanation": "보험 민원은 전화, 우편, 인터넷, 방문 등 다양한 방법으로 접수할 수 있다.", "category": "예상문제"}, {"id": 257, "question": "장애인 전용 보장성 보험의 세액 공제에서 근로소득자가 기본 공제 대상자 중 장애인을 ( )와 ( )로 할 경우 혜택이 적용된다.", "choices": ["피보험자, 보험 수익자", "계약자, 피보험자", "계약자, 수익자"], "answer": 1, "explanation": "장애인을 피보험자와 수익자로 한 경우에 세액 공제가 적용된다.", "category": "예상문제"}, {"id": 258, "question": "다음 중 보장성 보험료의 세액 공제가 가능한 경우는 무엇인가?", "choices": ["자영업을 영위하는 사람이 본인을 피보험자로 일반 보장성 보험에 가입하고 보험료를 납입한 경우", "계약 기간이 올해 12월부터 다음해 11월까지인 1년짜리 보장성 보험의 보험료를 올해 12월에 일시납한 경우", "계약자인 근로소득자가 태아를 피보험자로 하는 일반 보장성 보험에 가입한 경우"], "answer": 2, "explanation": "보장성 보험 세액 공제는 근로소득자만 해당되고 태아는 기본 공제 대상자에서 제외되므로 2번이 정답이다.", "category": "예상문제"}, {"id": 259, "question": "연금계좌 세액공제에서 종합소득금액 ( ) 이하 또는 총급여액 ( ) 이하인 경우 ( )의 세액 공제를 적용한다.", "choices": ["4,500만 원, 5,500만 원, 15%", "5,500만 원, 4,500만 원, 12%", "4,500만 원, 5,500만 원, 12%"], "answer": 1, "explanation": "종합소득 4,500만 원 이하 또는 총급여액 5,500만 원 이하인 경우 15%의 세액 공제 혜택을 제공한다.", "category": "예상문제"}, {"id": 260, "question": "다음 중 개인종합자산관리계좌(ISA)에 대한 설명으로 틀린 것은?", "choices": ["만료된 날로부터 60일 이내에 연금 계좌로 만료액을 납입한 경우 연금 계좌 납입에 포함해서 인정해 준다", "세액공제 한도 금액은 전환 금액의 10%와 300만 원 중 적은 금액까지 인정된다", "ISA 계좌에서 연금 계좌로 전환한 금액에 대한 공제율은 소득 금액과 상관없이 12%를 적용한다"], "answer": 3, "explanation": "연금 계좌 세액공제율은 소득 금액에 따라 12% 또는 15%가 적용된다.", "category": "예상문제"}, {"id": 261, "question": "다음 중 1주택 고령가구 연금 계좌 추가 납입에 대한 설명으로 틀린 것은?", "choices": ["거주자 또는 배우자 중 한 명이 60세 이상의 요건을 충족해야 한다", "부부 합산 1주택 소유의 요건을 충족해야 한다", "연금 계좌에 납입한 금액은 소득 공제 대상이다"], "answer": 3, "explanation": "연금 계좌에 납입한 금액은 소득 공제가 아닌 세액 공제 대상이다.", "category": "예상문제"}, {"id": 262, "question": "다음 중 근로소득 금액에서 세액 공제를 받을 수 없는 경우를 고르시오.", "choices": ["저축성 보험의 보험 차익", "연금 계좌 납입", "일반 보장성 보험료", "장애인 보장성 보험료"], "answer": 1, "explanation": "저축성 보험은 납입 시 세액 공제 혜택이 없다.", "category": "예상문제"}, {"id": 263, "question": "다음 중 종신형 연금 보험의 보험 차익 비과세 요건으로 틀린 것은?", "choices": ["연금 외의 형태로 보험금 등을 지급하지 않을 것", "계약자와 피보험자 및 수익자가 동일한 계약으로서 최초 연금 지급 개시 이후 사망일 전에 계약을 중도 해지할 수 없을 것", "매년 수령하는 연금액이 전체 연금 평가액을 기대여명으로 나눈 금액의 두 배 이내일 것"], "answer": 3, "explanation": "한도는 두 배가 아닌 세 배 이내여야 한다.", "category": "예상문제"}, {"id": 264, "question": "단체 순수 보장성 보험에서 종업원을 피보험자와 수익자로 하고 기업이 보험료를 납입하는 경우, 연간 ( ) 이하의 금액은 복리후생비로 인정된다.", "choices": ["50만 원", "70만 원", "100만 원"], "answer": 2, "explanation": "연간 70만 원 이하의 보험료는 복리후생비로 인정된다.", "category": "예상문제"}, {"id": 265, "question": "증여세는 어떤 과세 방식을 적용하는가?", "choices": ["완전 포괄주의", "열거주의", "유형별 포괄주의"], "answer": 1, "explanation": "상속세, 증여세, 법인세는 완전 포괄주의를 적용한다.", "category": "예상문제"}, {"id": 266, "question": "다음 중 장애인을 수익자로 하는 보험금의 증여세 비과세 한도는 얼마인가?", "choices": ["연간 4천만 원", "연간 2천만 원", "연간 5천만 원"], "answer": 1, "explanation": "장애인을 수익자로 하는 보험금은 연간 4천만 원까지 증여세가 비과세된다.", "category": "예상문제"}, {"id": 267, "question": "다음 중 분류 과세에 해당하지 않는 것을 고르시오.", "choices": ["퇴직 소득", "양도 소득", "연금 소득"], "answer": 3, "explanation": "분류 과세는 퇴직 소득과 양도 소득이며, 연금 소득은 종합 과세가 원칙이다.", "category": "예상문제"}, {"id": 268, "question": "다음 중 연금 소득의 분리 과세에 대한 설명으로 틀린 것은?", "choices": ["연금 수령 시점에 나이와 인출 요건을 충족해야 된다", "3%에서 5%의 저율 분리 과세 혜택을 받기 위해서는 연간 연금 수령액이 1,500만 원 초과인 경우에 선택이 가능하다", "이연 퇴직 소득을 연금으로 수령하는 경우 일시금 수령 대비 50%~70% 수준의 세율을 적용한다"], "answer": 2, "explanation": "저율 분리 과세는 연간 연금 수령액이 1,500만 원 이하인 경우에 선택 가능하다.", "category": "예상문제"}, {"id": 269, "question": "다음 표에 간편 장부 대상자에 대한 내용으로 맞는 것은?", "choices": ["신규 사업 개시 설계사와 전년도 수입 금액 5,000만 원 미만인 설계사", "신규 사업 개시 설계사와 전년도 수입 금액 7,500만 원 미만인 설계사", "전년도 수입 금액 1억 원 미만인 설계사"], "answer": 2, "explanation": "간편 장부 대상자는 신규 사업 개시 설계사 또는 전년도 수입 금액 7,500만 원 미만인 설계사이다.", "category": "예상문제"}, {"id": 270, "question": "보험 설계사의 원천 징수와 관련하여 이미 납입한 세액을 무엇이라 하는가?", "choices": ["기납부 세액", "예정 세액", "확정 세액"], "answer": 1, "explanation": "회사가 매월 수당 지급 시 미리 원천 징수한 세액을 기납부 세액이라고 한다.", "category": "예상문제"}, {"id": 271, "question": "다음 중 생명보험 관련 법규에 대한 설명으로 맞게 짝지어진 것은?", "choices": ["보험업법 - 계약 관계를 규정", "상법 - 허가 및 감독의 근거", "독점 규제 및 공정 거래에 관한 법률 - 금융산업 구조 개선", "자본 시장과 금융 투자에 관한 법률 - 변액보험"], "answer": 4, "explanation": "허가 및 감독은 보험업법, 계약 관계는 상법, 변액보험은 자본 시장과 금융 투자에 관한 법률이다.", "category": "예상문제"}, {"id": 272, "question": "다음 중 생명보험 계약과 손해보험 계약에 대한 설명으로 맞는 것은?", "choices": ["손해보험의 보장 대상은 사람의 생존과 사망이다", "생명보험의 보험 기간은 대체로 단기 확정 기간이다", "생명보험의 주된 보상 원칙은 약정된 금액을 지급하는 정액 보상이 원칙이다", "손해보험 계약을 취급하는 회사는 손해보험 회사이다"], "answer": 4, "explanation": "손해보험 계약을 취급하는 회사는 손해보험 회사이다.", "category": "예상문제"}, {"id": 273, "question": "다음 중 생명보험 계약의 특성에 대한 설명으로 틀린 것은? (부합계약성, 사행계약성, 낙성계약성)", "choices": ["보험 계약의 부합 계약성으로 인하여 계약자 보호를 위한 규제 및 감독이 필요하다", "도박, 복권과 마찬가지로 사행 계약성이 인정된다", "계약 당사자 간의 의사 표시의 합치만으로 계약이 성립되며 이를 선의 계약성이라 한다"], "answer": 3, "explanation": "의사 표시의 합치로 계약이 성립되는 것은 선의 계약성이 아닌 낙성 계약성이다.", "category": "예상문제"}, {"id": 274, "question": "다음 중 생명보험 계약의 특성에 대한 설명으로 틀린 것은? (상행위성)", "choices": ["계약 당사자 간의 의사 표시의 합치가 있으면 계약이 성립된다", "계약전 알릴 의무가 있는 계약 당사자는 계약자와 피보험자이다", "보험 계약 체결 시 해당 약관을 전체로서 동의하거나 거절의 선택밖에는 할 수 없다", "보험회사는 상인이므로 상법을 적용하지만 계약자는 그렇지 않다"], "answer": 4, "explanation": "한쪽이라도 상인이면 둘 다 상법을 적용받으므로 계약자에게도 상법이 적용된다.", "category": "예상문제"}, {"id": 275, "question": "다음 중 보험회사에서 보험 상품을 개발할 때 기초 서류가 아닌 것은?", "choices": ["보험 약관", "사업 방법서", "보험료 및 해약 환급금 산출 방법서", "상품 설명서"], "answer": 4, "explanation": "기초 서류는 사업 방법서, 산출 방법서, 보험 약관이며 상품 설명서는 기초 서류가 아니다.", "category": "예상문제"}, {"id": 276, "question": "다음 중 보장을 추가 확대하기 위한 특약에 해당하지 않는 것은?", "choices": ["암 특약", "상해 특약", "질병 특약", "선지급 서비스 특약"], "answer": 4, "explanation": "선지급 서비스 특약은 제도성 특약에 해당한다.", "category": "예상문제"}, {"id": 277, "question": "다음 중 특약의 분류에서 확장성에 따른 분류에 해당하는 것은?", "choices": ["독립 특약과 종속 특약", "의무부가 특약과 선택 특약", "보장 특약과 제도성 특약", "제도성 특약과 선택 특약"], "answer": 1, "explanation": "확장성에 따른 분류는 독립 특약과 종속 특약이다.", "category": "예상문제"}, {"id": 278, "question": "다음 중 생명보험 상품의 분류가 틀리게 연결된 것은?", "choices": ["계약자의 수에 따라 개인보험과 단체보험으로 구분", "가입의 주목적에 따라 보장성 보험과 저축성 보험으로 구분", "보험 사고에 따라 생존보험, 사망보험, 생사혼합보험으로 구분"], "answer": 1, "explanation": "개인보험과 단체보험은 계약자의 수가 아닌 피보험자의 수에 따라 구분한다.", "category": "예상문제"}, {"id": 279, "question": "다음 중 정기보험과 종신보험의 가장 큰 차이점은 무엇인가?", "choices": ["보험료", "보장 내용", "보험 기간", "납입 방식"], "answer": 3, "explanation": "정기보험은 정해진 기간, 종신보험은 종신토록 보장하므로 가장 큰 차이는 보험 기간이다.", "category": "예상문제"}, {"id": 280, "question": "다음 중 자산 연계형 보험에 대한 설명으로 틀린 것은?", "choices": ["모두 일반 계정에서 운영한다", "변액 보험보다 다소 안정적이다", "최저보증 이율을 가지고 있다"], "answer": 1, "explanation": "자산 연계형 보험은 일반 계정과 특별 계정 모두에서 운영 가능하다.", "category": "예상문제"}, {"id": 281, "question": "해약 환급금 수준에 따른 분류로, 보험 기간 중 일정 기간 동안 해약 환급금을 적게 주는 조건으로 보험료를 낮게 책정한 상품은?", "choices": ["무해지 환급형 보험", "표준형 보험", "저해지 환급형 보험"], "answer": 3, "explanation": "해약 환급금을 적게 주는 조건의 상품은 저해지 환급형 보험이다.", "category": "예상문제"}, {"id": 282, "question": "다음 중 유니버셜 보험에 대한 설명으로 틀린 것은?", "choices": ["중도 인출이 가능하다", "보험료의 추가 납입이 가능하다", "일정 기간 동안 보험료를 내지 않는 것도 가능하다", "저축성 보험에만 유니버셜 기능이 가능하다"], "answer": 4, "explanation": "유니버셜 기능은 저축성과 보장성 보험 모두 가능하다.", "category": "예상문제"}, {"id": 283, "question": "다음 중 장애인 보험에 대한 설명으로 틀린 것은?", "choices": ["보험회사는 정당한 이유 없이 장애인의 보험 계약 청약을 거절할 수 없다", "보장 내용이 유사한 일반 보험에 비해 장애인 전용 보험은 보험료가 상대적으로 더 저렴하다", "장애인을 피보험자로 가입하는 보험 계약의 보험금은 연간 4천만 원 한도 내에서 증여세를 비과세한다", "장애인 전용 보장성 보험의 보험료는 세액 공제를 15%까지 받을 수 있다"], "answer": 3, "explanation": "증여세 비과세는 장애인을 피보험자가 아닌 수익자로 하는 경우에 적용된다.", "category": "예상문제"}, {"id": 284, "question": "다음 중 세제적격 연금 저축의 보험료 납입 한도는 얼마인가?", "choices": ["연간 600만 원", "연간 900만 원", "연간 1,200만 원", "연간 1,800만 원"], "answer": 4, "explanation": "세제적격 연금 저축의 납입 한도는 연간 1,800만 원이다.", "category": "예상문제"}, {"id": 285, "question": "다음 중 세제적격 연금 저축에 대한 설명으로 맞는 것은?", "choices": ["최소 보험료 납입 기간은 10년이다", "가입 나이에 제한이 있다", "근거법령은 소득세법이며 공제 방식은 세액 공제 방식이다"], "answer": 3, "explanation": "최소 납입 기간은 5년이고, 가입 나이 제한은 없으며, 소득세법에 근거한 세액 공제 방식이다.", "category": "예상문제"}, {"id": 286, "question": "다음 중 퇴직 연금에 대한 설명으로 맞는 것은?", "choices": ["운용 실적과 관계 없이 퇴직 급여액은 변동하지 않는다", "DC형(확정 기여형)은 퇴직시에 회사가 퇴직금을 지급하지 않아도 된다", "2005년부터 퇴직 연금은 전 업장으로 확대되어 시행 중이다", "채권, 주식, 부동산 투자 등 다양한 방법으로 적립금을 운용할 수 있다"], "answer": 2, "explanation": "확정 기여형은 회사가 매월 금융기관에 납부하면 의무를 다하므로 퇴직 시 별도 지급이 필요 없다.", "category": "예상문제"}, {"id": 287, "question": "다음 중 변액 보험에 대한 설명으로 맞는 것은?", "choices": ["생명보험 협회 홈페이지에서 계약자별 투자 실적을 확인할 수 있다", "적립된 금액을 평가하여 매월 1회 배당금을 지급한다", "일반 계정에서 운영한다", "투자 실적에 따라 보험금과 해약 환급금이 변동한다"], "answer": 4, "explanation": "변액 보험은 투자 실적에 따라 보험금과 해약 환급금이 변동한다.", "category": "예상문제"}, {"id": 288, "question": "다음 중 변액 보험에 대한 설명으로 틀린 것은?", "choices": ["투자 실적에 따라 보험금과 해약 환급금이 변동한다", "계약자 자산에 비례하여 공평하게 투자 손익을 배분하기 위해 특별 계정에서 운용한다", "계약자 적립금은 특별 계정의 평가 등에 의하여 매일 변동될 수 있다", "저축성 변액 보험은 피보험자가 사망하는 경우 기납입 보험료를 지급한다"], "answer": 4, "explanation": "저축성 변액 보험 사망 시에는 기본 보험금+적립금과 기납입 보험료 중 큰 금액을 지급한다.", "category": "예상문제"}, {"id": 289, "question": "종신보험의 보험료 추가 납입 및 중도인출 기능을 결합한 종합 금융형 상품이며, 펀드의 운용 실적에 따라 보험금이 변동하는 상품은?", "choices": ["변액 종신보험", "유니버셜 보험", "변액 유니버셜 보험"], "answer": 3, "explanation": "추가 납입/중도인출은 유니버셜, 펀드 실적 변동은 변액의 특징이므로 변액 유니버셜 보험이다.", "category": "예상문제"}, {"id": 290, "question": "다음 중 변액 연금보험과 퇴직 연금보험의 공통점으로 맞는 것은?", "choices": ["두 상품 모두 특별 계정에서 운영된다", "생명보험 회사에서만 판매할 수 있다", "연금 계좌의 세액 공제가 가능하다", "연금 재원을 최저보증한다"], "answer": 1, "explanation": "두 상품 모두 특별 계정에서 운영된다.", "category": "예상문제"}, {"id": 291, "question": "다음 중 제3보험에 대한 설명으로 틀린 것은?", "choices": ["제3보험은 77년 보험법 전면 개정시 처음으로 법체계에 편입되었다", "제3보험은 사람의 신체에 대한 보험이라는 의미에서 생명보험의 성격을 가지고 있다", "실손 보상적 급부를 보상한다는 측면에서 손해보험의 성격도 가지고 있다", "생명보험에 해당하는 모든 종목의 허가를 받은 생명보험 회사는 제3보험업에 겸영이 가능하다"], "answer": 1, "explanation": "제3보험은 77년이 아닌 2003년 보험업법 개정 시 법체계에 편입되었다.", "category": "예상문제"}, {"id": 292, "question": "보험업법상 제3보험 상품 중 상법에 별도로 분류되어 있지 않은 보험은?", "choices": ["상해", "간병", "질병"], "answer": 2, "explanation": "상법에는 상해보험과 질병보험은 있지만 간병보험은 없다.", "category": "예상문제"}, {"id": 293, "question": "다음 중 보험업법에서 구분하는 보험업의 구분에 해당하지 않는 것은?", "choices": ["생명보험업", "손해보험업", "제3보험업", "인보험업"], "answer": 4, "explanation": "보험업법은 생명보험업, 손해보험업, 제3보험업으로 구분하며 인보험업은 상법의 구분이다.", "category": "예상문제"}, {"id": 294, "question": "다음 중 손해보험 회사에서 판매할 수 없는 질병 사망 특약은?", "choices": ["가입금액 1억 원, 보험기간 80세", "가입금액 1억 원, 보험기간 90세", "가입금액 2억 원, 보험기간 70세", "가입금액 2억 원, 보험기간 80세"], "answer": 2, "explanation": "손해보험 회사는 가입 금액 최대 2억 원, 보험 기간 최대 80세까지이므로 90세는 불가하다.", "category": "예상문제"}, {"id": 295, "question": "다음 중 피보험 이익에 대한 설명으로 틀린 것은?", "choices": ["보험 계약의 목적을 금전으로 환산할 수 있어야 된다", "생명보험에는 적용되는 개념이 아니다", "물건의 가치가 사고 후에 확정되어 있어야 한다", "손해보험 회사에서는 필수적인 요소이다"], "answer": 3, "explanation": "물건의 가치는 사고 후가 아닌 사고 전에 확정되어 있어야 한다.", "category": "예상문제"}, {"id": 296, "question": "제3보험 상품에서 약관상 보장하지 않는 원인으로 피보험자가 사망할 경우 지급하는 것은?", "choices": ["보험금", "해약 환급금", "기납입 보험료", "계약자 적립액"], "answer": 4, "explanation": "제3보험에서 보장 외 사망 시 계약자 적립액을 지급하고 계약이 소멸한다.", "category": "예상문제"}, {"id": 297, "question": "제3보험 상품 중 ( ) 보상 급부가 있는 계약은 사고 발생 시 ( ) 보상하여 실제 부담 금액을 초과하지 않게 보험금이 지급된다.", "choices": ["정액, 균등", "실손, 비례", "실손, 정액"], "answer": 2, "explanation": "실손 보상 급부가 있는 계약은 비례 보상하여 초과 지급을 방지한다.", "category": "예상문제"}, {"id": 298, "question": "다음 중 생명보험과 상해 보험에 대한 설명으로 틀린 것은?", "choices": ["생명보험의 보험 사고는 사람의 생존과 사망이다", "생명보험은 계약자와 보험 회사간에 다툼이 생길 여지가 크다", "상해보험의 보험 사고는 사람의 신체에 입은 상해이다", "상해 보험은 인정 여부가 중요한 쟁점이 된다"], "answer": 2, "explanation": "생명보험은 다툼의 여지가 적고 상해보험이 우연성, 급격성, 외래성 요건으로 다툼의 여지가 크다.", "category": "예상문제"}, {"id": 299, "question": "상해보험에서 보장받을 수 있는 경우는? (갑: 급성 중독, 을: 질병이 원인인 상해, 병: 우연한 사고)", "choices": ["갑, 을", "갑, 병", "을, 병"], "answer": 2, "explanation": "상해보험은 원인이 상해여야 보상되므로 급성 중독(갑)과 우연한 사고(병)만 보장된다.", "category": "예상문제"}, {"id": 300, "question": "직업 또는 직무의 변경 통지를 게을리한 경우 보험 회사는 그 사실을 알게 된 날로부터 ( ) 이내에 보험료 증액 청구 또는 계약 해지를 할 수 있다.", "choices": ["1개월", "3개월", "6개월"], "answer": 1, "explanation": "보험회사는 통지 위반을 알게 된 날로부터 1개월 이내에 결정해야 한다.", "category": "예상문제"}, {"id": 301, "question": "다음 중 질병 보험에 대한 설명으로 틀린 것은?", "choices": ["보장성 보험에 해당된다", "만기 환급형으로 설계가 가능하다", "어린이 보험은 질병과 상해 보험에 모두 해당된다", "직업별 위험 등급에 따라 위험률을 차등 적용한다"], "answer": 4, "explanation": "직업별 위험 등급은 질병 보험이 아닌 상해 보험의 특징이다.", "category": "예상문제"}, {"id": 302, "question": "다음 중 암보험에 대한 설명으로 맞는 것은?", "choices": ["제자리암의 경우 일정 비율의 소액으로 보험금이 지급될 수 있다", "암보험은 순수 보장형 상품으로만 판매된다", "암보험은 3대 주요암, 고액암, 소액암만 보장한다", "보험금 지급 사유 발생 전 피보험자 사망 시 보험금을 지급한다"], "answer": 1, "explanation": "제자리암(상피내암)은 일반 보험금 전액이 아닌 소액으로 지급될 수 있다.", "category": "예상문제"}, {"id": 303, "question": "다음 중 암의 직접적인 치료에 해당하는 것은?", "choices": ["항암 방사선 치료와 항암 약물 치료", "의학적으로 유효성이 입증되지 않은 치료", "면역력 강화 치료와 명상 치료"], "answer": 1, "explanation": "항암 방사선 치료와 항암 약물 치료가 암의 직접적인 치료에 해당한다.", "category": "예상문제"}, {"id": 304, "question": "다음 중 실손 보상 급부에 대한 설명으로 맞는 것은?", "choices": ["보험금을 보험료에 비례하여 지급한다", "모든 제3보험 상품이 실손 보상 상품이다", "사망 보험금도 비례보상 대상이다", "사망 보험금은 비례보상 대상이 아니고 정액 보상이다"], "answer": 4, "explanation": "사망 보험금은 비례보상이 아닌 정액 보상이다.", "category": "예상문제"}, {"id": 305, "question": "다음 중 4세대 실손 의료 보험에 대한 설명으로 틀린 것은?", "choices": ["일부 비급여 항목은 과잉의료 이용 방지를 위해 보장을 제한하고 있다", "도수 치료는 비급여 보장 특약에서 보장받을 수 있다", "주계약과 특약의 자기 부담금 비율은 서로 동일하다", "불임질환은 급여 항목이라서 3세대 대비 보장이 확대되었다"], "answer": 3, "explanation": "주계약(급여) 자기 부담금 20%, 특약(비급여) 자기 부담금 30%로 동일하지 않다.", "category": "예상문제"}, {"id": 306, "question": "다음 중 실손 의료보험 비급여 진료 비용에 대한 설명으로 맞는 것은?", "choices": ["의료 정보원 홈페이지와 건강이음 앱에서 공개하고 있다", "비급여 진료 비용은 건강보험 심사 평가원에서 결정한다", "매주 비급여 진료 비용을 조사하여 발표하고 있다", "소비자는 비급여 진료 비용을 미리 확인하여 의료 기관 선택에 도움을 받을 수 있다"], "answer": 4, "explanation": "비급여 진료 비용은 심사평가원 홈페이지에서 공개되며 소비자가 미리 확인 가능하다.", "category": "예상문제"}, {"id": 307, "question": "다음 중 실손 의료 보험에 대한 설명으로 틀린 것은?", "choices": ["3세대 실손 계약자가 원하면 4세대로 전환할 수 있다", "4세대 전환 후 1년 이내에 보험금 수령이 없으면 기존 3세대로 돌아갈 수 있다", "4세대 전환 시 보험료가 더 저렴하다"], "answer": 2, "explanation": "기존 상품으로 돌아갈 수 있는 기간은 1년이 아닌 6개월 이내이다.", "category": "예상문제"}, {"id": 308, "question": "다음 중 어린이 보험에서 설계가 가능한 보장으로만 나열한 것은?", "choices": ["안전 사고, 질병 보장", "안전 사고, 사망 보장", "질병, 사망 보장", "상해, 사망 보장"], "answer": 1, "explanation": "어린이 보험에서는 사망을 보장하지 않으므로 안전 사고와 질병만 가능하다.", "category": "예상문제"}, {"id": 309, "question": "다음 중 간병보험에서 면책되지 않는 것은?", "choices": ["의사의 처방이 없는 약물 투여", "면책 기간 중에 발생한 재해로 인한 사고", "정신 분열병으로 인한 인지 기능의 장애", "알코올 중독"], "answer": 2, "explanation": "재해로 인한 사고는 면책 기간 중에도 보장해 준다.", "category": "예상문제"}, {"id": 310, "question": "다음 중 위험률 변동 제도에 대한 설명으로 틀린 것은?", "choices": ["민영 장기 간병보험에서 채택하고 있는 제도이다", "실제 위험 발생률이 예측 위험률과 상이한 경우 조정하는 제도이다", "금융위원회의 인가를 받아서 시행해야 한다", "보험 기간 종료 후에 보험료를 재산정하여 정산된 보험료를 되돌려 주는 제도이다"], "answer": 4, "explanation": "간병보험은 피보험자 사망까지가 보험 기간이므로 기간 종료 후가 아닌 기간 중에 재산정해야 한다.", "category": "예상문제"}];